import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import IconButton from "@mui/material/IconButton";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MenuIcon from "@mui/icons-material/Menu";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import DashboardIcon from "@mui/icons-material/Dashboard";
import HomeIcon from "@mui/icons-material/Home";
import AdminPanelSettingsIcon from "@mui/icons-material/AdminPanelSettings";
import ArrowRightIcon from "@mui/icons-material/ArrowRight";
import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";

function Navbar() {
  const location = useLocation();
  const [anchorEl, setAnchorEl] = useState(null);
  const [productsMenu, setProductsMenu] = useState(null);
  const [industryMenu, setIndustryMenu] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  // ✅ Verificar si el usuario es admin mediante token
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split(".")[1]));
        if (payload.role === "admin") setIsAdmin(true);
      } catch {
        setIsAdmin(false);
      }
    }
  }, []);

  // ✅ Actualizar cantidad del carrito
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart") || "[]");
    setCartCount(storedCart.reduce((sum, item) => sum + (item.quantity || 1), 0));
  }, [location]);

  const linkStyle = (path) => ({
    color: "#fff",
    marginRight: "15px",
    textDecoration: location.pathname === path ? "underline" : "none"
  });

  return (
    <AppBar position="static" sx={{ backgroundColor: "#1e1e1e" }}>
      <Toolbar>
        {/* Logo */}
        <Typography
          variant="h6"
          component={Link}
          to="/"
          sx={{
            flexGrow: 1,
            color: "#fff",
            textDecoration: "none",
            fontWeight: "bold",
            fontSize: "1.3rem"
          }}
        >
          🛒 Tienda Titilab
        </Typography>

        {/* Menú hamburguesa para móviles */}
        <IconButton
          edge="start"
          color="inherit"
          aria-label="menu"
          sx={{ display: { xs: "flex", md: "none" } }}
          onClick={(e) => setAnchorEl(e.currentTarget)}
        >
          <MenuIcon />
        </IconButton>
        <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={() => setAnchorEl(null)}>
          {/* Productos */}
          <MenuItem onClick={(e) => setProductsMenu(e.currentTarget)}>
            Productos <ArrowDropDownIcon sx={{ ml: 1 }} />
          </MenuItem>
          <Menu anchorEl={productsMenu} open={Boolean(productsMenu)} onClose={() => setProductsMenu(null)}>
            {["Microcontroladores", "Arduino", "ESP32", "Raspberry", "Sensores", "Componentes"].map((cat) => (
              <MenuItem
                key={cat}
                component={Link}
                to={`/productos/${cat.toLowerCase()}`}
                onClick={() => setProductsMenu(null)}
              >
                <ArrowRightIcon fontSize="small" /> {cat}
              </MenuItem>
            ))}
          </Menu>

          {/* Industria */}
          <MenuItem onClick={(e) => setIndustryMenu(e.currentTarget)}>
            Industria <ArrowDropDownIcon sx={{ ml: 1 }} />
          </MenuItem>
          <Menu anchorEl={industryMenu} open={Boolean(industryMenu)} onClose={() => setIndustryMenu(null)}>
            {["Minería", "Pesquería", "Ganadería", "Construcción"].map((cat) => (
              <MenuItem
                key={cat}
                component={Link}
                to={`/industria/${cat.toLowerCase()}`}
                onClick={() => setIndustryMenu(null)}
              >
                <ArrowRightIcon fontSize="small" /> {cat}
              </MenuItem>
            ))}
          </Menu>

          {/* Otras páginas */}
          <MenuItem component={Link} to="/impresion3d" onClick={() => setAnchorEl(null)}>
            Impresión 3D
          </MenuItem>
          <MenuItem component={Link} to="/cursos" onClick={() => setAnchorEl(null)}>
            Cursos
          </MenuItem>
          <MenuItem component={Link} to="/cart" onClick={() => setAnchorEl(null)}>
            Carrito ({cartCount})
          </MenuItem>
          {isAdmin && (
            <MenuItem component={Link} to="/admin" onClick={() => setAnchorEl(null)}>
              Admin
            </MenuItem>
          )}
        </Menu>

        {/* Botones escritorio */}
        <div style={{ display: "flex", alignItems: "center" }}>
          <Button component={Link} to="/" sx={{ ...linkStyle("/"), display: { xs: "none", md: "inline-flex" } }} startIcon={<HomeIcon />}>
            Inicio
          </Button>

          {/* Productos Dropdown */}
          <Button
            sx={{ color: "#fff", display: { xs: "none", md: "inline-flex" } }}
            onClick={(e) => setProductsMenu(e.currentTarget)}
          >
            Productos <ArrowDropDownIcon />
          </Button>

          {/* Industria Dropdown */}
          <Button
            sx={{ color: "#fff", display: { xs: "none", md: "inline-flex" } }}
            onClick={(e) => setIndustryMenu(e.currentTarget)}
          >
            Industria <ArrowDropDownIcon />
          </Button>

          <Button component={Link} to="/cart" sx={{ ...linkStyle("/cart"), display: { xs: "none", md: "inline-flex" } }} startIcon={<ShoppingCartIcon />}>
            Carrito ({cartCount})
          </Button>
          <Button component={Link} to="/dashboards" sx={{ ...linkStyle("/dashboards"), display: { xs: "none", md: "inline-flex" } }} startIcon={<DashboardIcon />}>
            IoT Dashboards
          </Button>
          {isAdmin && (
            <Button component={Link} to="/admin" sx={{ ...linkStyle("/admin"), display: { xs: "none", md: "inline-flex" } }} startIcon={<AdminPanelSettingsIcon />}>
              Admin
            </Button>
          )}
        </div>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
