// src/context/CartContext.js
import React, { createContext, useState, useEffect } from "react";

// ðŸ“Œ Crear contexto
export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  // ðŸ“Œ Cargar carrito desde localStorage al iniciar
  useEffect(() => {
    const storedCart = localStorage.getItem("cart");
    if (storedCart) {
      setCart(JSON.parse(storedCart));
    }
  }, []);

  // ðŸ“Œ Guardar carrito en localStorage cada vez que cambie
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // ðŸ“Œ Agregar producto al carrito evitando duplicados
  const addToCart = (product) => {
    setCart((prevCart) => {
      const productId = String(product._id);
      const existingIndex = prevCart.findIndex(
        (item) => String(item._id) === productId
      );

      if (existingIndex !== -1) {
        // Producto ya existe â†’ aumentar cantidad
        const updatedCart = [...prevCart];
        updatedCart[existingIndex] = {
          ...updatedCart[existingIndex],
          quantity: updatedCart[existingIndex].quantity + 1
        };
        return updatedCart;
      } else {
        // Nuevo producto â†’ agregar con cantidad = 1
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  // ðŸ“Œ Eliminar producto del carrito
  const removeFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter((item) => item._id !== productId));
  };

  // ðŸ“Œ Vaciar carrito
  const clearCart = () => {
    setCart([]);
  };

  return (
    <CartContext.Provider
      value={{ cart, addToCart, removeFromCart, clearCart }}
    >
      {children}
    </CartContext.Provider>
  );
};
