import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  TextField,
  Button,
  Card,
  CardContent,
  Divider,
  RadioGroup,
  FormControlLabel,
  Radio,
  Alert
} from "@mui/material";

function Checkout() {
  const [cart, setCart] = useState([]);
  const [shipping, setShipping] = useState(15); // Valor inicial Olva
  const [rucValid, setRucValid] = useState(null);
  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    direccion: "",
    telefono: "",
    ruc: "",
    empresa: "",
    envio: "Olva"
  });

  // üìå Cargar carrito
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  // üìå Calcular subtotal
  const getSubtotal = () =>
    cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  // üìå Calcular total
  const getTotal = () => (getSubtotal() + shipping).toFixed(2);

  // üìå Manejar cambios
  const handleChange = async (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));

    if (name === "envio") {
      if (value === "Olva") setShipping(15);
      if (value === "Shalom") setShipping(20);
    }

    // Validar RUC en tiempo real
    if (name === "ruc" && value.length === 11) {
      validarRUC(value);
    }
  };

  // üìå Validaci√≥n y consulta a API SUNAT
  const validarRUC = async (ruc) => {
    if (!/^[0-9]{11}$/.test(ruc)) {
      setRucValid(false);
      return;
    }

    try {
      // üöÄ API p√∫blica para validar RUC
      const res = await fetch(`https://api.apis.net.pe/v1/ruc?numero=${ruc}`, {
        headers: {
          Authorization: "Bearer test" // ‚ö†Ô∏è Reemplaza con tu token real de API's Per√∫
        }
      });

      if (!res.ok) {
        setRucValid(false);
        return;
      }

      const data = await res.json();
      if (data && data.nombre) {
        setFormData((prev) => ({ ...prev, empresa: data.nombre }));
        setRucValid(true);
      } else {
        setRucValid(false);
      }
    } catch (error) {
      console.error("Error validando RUC:", error);
      setRucValid(false);
    }
  };

  // üìå Enviar pedido
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.nombre || !formData.email || !formData.direccion) {
      alert("Por favor, completa todos los campos obligatorios.");
      return;
    }

    if (formData.ruc && !rucValid) {
      alert("El RUC ingresado no es v√°lido.");
      return;
    }

    alert(
      `‚úÖ Pedido realizado con √©xito\nGracias por tu compra, ${formData.nombre}\nEnv√≠o: ${formData.envio} (S/ ${shipping})\nTotal: S/ ${getTotal()}`
    );

    localStorage.removeItem("cart");
    window.location.href = "/";
  };

  if (cart.length === 0) {
    return (
      <Container sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="h5">üõí No hay productos en tu carrito</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ mt: 4, mb: 6 }}>
      <Typography variant="h4" gutterBottom>
        üßæ Finalizar Compra
      </Typography>

      <Grid container spacing={4}>
        {/* Formulario */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>
            Datos de env√≠o y facturaci√≥n
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Nombre completo"
              name="nombre"
              value={formData.nombre}
              onChange={handleChange}
              fullWidth
              margin="normal"
              required
            />
            <TextField
              label="Correo electr√≥nico"
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              fullWidth
              margin="normal"
              required
            />
            <TextField
              label="Direcci√≥n"
              name="direccion"
              value={formData.direccion}
              onChange={handleChange}
              fullWidth
              margin="normal"
              required
            />
            <TextField
              label="Tel√©fono"
              name="telefono"
              value={formData.telefono}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <TextField
              label="RUC"
              name="ruc"
              value={formData.ruc}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            {rucValid === false && (
              <Alert severity="error">‚ùå RUC no v√°lido</Alert>
            )}
            {rucValid === true && (
              <Alert severity="success">‚úÖ RUC v√°lido - Empresa encontrada</Alert>
            )}

            <TextField
              label="Nombre de empresa"
              name="empresa"
              value={formData.empresa}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <Typography variant="h6" sx={{ mt: 2 }}>
              M√©todo de env√≠o
            </Typography>
            <RadioGroup
              name="envio"
              value={formData.envio}
              onChange={handleChange}
            >
              <FormControlLabel
                value="Olva"
                control={<Radio />}
                label="Delivery - Olva: S/ 15.00"
              />
              <FormControlLabel
                value="Shalom"
                control={<Radio />}
                label="Delivery - Shalom: S/ 20.00"
              />
            </RadioGroup>

            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              sx={{ mt: 3 }}
            >
              Realizar Pedido
            </Button>
          </form>
        </Grid>

        {/* Resumen */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>
            Resumen de compra
          </Typography>
          <Card sx={{ boxShadow: 4 }}>
            <CardContent>
              {cart.map((item) => (
                <div key={item._id} style={{ marginBottom: "10px" }}>
                  <Typography variant="body1">
                    {item.name} x {item.quantity}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    S/ {(item.price * item.quantity).toFixed(2)}
                  </Typography>
                  <Divider sx={{ my: 1 }} />
                </div>
              ))}

              <Typography variant="body1" sx={{ mt: 2 }}>
                Subtotal: <strong>S/ {getSubtotal().toFixed(2)}</strong>
              </Typography>
              <Typography variant="body1">
                Env√≠o ({formData.envio}): <strong>S/ {shipping.toFixed(2)}</strong>
              </Typography>
              <Divider sx={{ my: 2 }} />
              <Typography variant="h6" align="right" color="primary">
                Total: S/ {getTotal()}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Checkout;
