// src/pages/Cart.js
import React, { useContext, useEffect, useState } from "react";
import {
  Container,
  Typography,
  Card,
  CardMedia,
  CardContent,
  IconButton,
  Button,
  Box,
  Divider
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { Link } from "react-router-dom";
import { CartContext } from "../context/CartContext";

function Cart() {
  const { cart, addToCart, removeFromCart, clearCart } = useContext(CartContext);
  const [total, setTotal] = useState(0);

  // üìå Calcular total cada vez que cambie el carrito
  useEffect(() => {
    const totalPrice = cart.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    setTotal(totalPrice);
  }, [cart]);

  // üìå Disminuir cantidad
  const decreaseQuantity = (product) => {
    if (product.quantity > 1) {
      const updatedProduct = { ...product, quantity: product.quantity - 1 };
      removeFromCart(product._id); // Eliminar producto actual
      for (let i = 0; i < updatedProduct.quantity; i++) {
        addToCart(updatedProduct); // Reagregar con nueva cantidad
      }
    } else {
      removeFromCart(product._id);
    }
  };

  return (
    <Container sx={{ mt: 4, mb: 6 }}>
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          fontWeight: "bold",
          color: "#333",
          display: "flex",
          alignItems: "center"
        }}
      >
        <ShoppingCartIcon sx={{ mr: 1, fontSize: 32, color: "#1976d2" }} />
        Carrito de Compras
      </Typography>

      {cart.length === 0 ? (
        <Typography variant="h6" sx={{ color: "#777", mt: 3 }}>
          Tu carrito est√° vac√≠o.{" "}
          <Link
            to="/"
            style={{ textDecoration: "none", color: "#1976d2" }}
          >
            Ir a la tienda
          </Link>
        </Typography>
      ) : (
        <>
          {cart.map((item) => (
            <Card
              key={item._id}
              sx={{
                display: "flex",
                alignItems: "center",
                mb: 2,
                boxShadow: 4,
                borderRadius: 2,
                transition: "0.3s",
                "&:hover": { boxShadow: 8 }
              }}
            >
              <CardMedia
                component="img"
                sx={{
                  width: 140,
                  height: 140,
                  objectFit: "contain",
                  backgroundColor: "#f9f9f9",
                  borderRight: "1px solid #eee"
                }}
                image={item.image}
                alt={item.name}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                  {item.name}
                </Typography>
                <Typography
                  variant="body1"
                  sx={{ color: "#1976d2", fontWeight: "bold" }}
                >
                  S/ {item.price.toFixed(2)}
                </Typography>

                <Box sx={{ display: "flex", alignItems: "center", mt: 1 }}>
                  <Typography
                    variant="body2"
                    sx={{ mr: 1, fontWeight: "bold" }}
                  >
                    Cantidad:
                  </Typography>
                  <IconButton
                    onClick={() => decreaseQuantity(item)}
                    size="small"
                    sx={{
                      backgroundColor: "#eee",
                      "&:hover": { backgroundColor: "#ddd" }
                    }}
                  >
                    <RemoveIcon />
                  </IconButton>
                  <Typography
                    variant="body1"
                    sx={{
                      mx: 1,
                      minWidth: "24px",
                      textAlign: "center"
                    }}
                  >
                    {item.quantity}
                  </Typography>
                  <IconButton
                    onClick={() => addToCart(item)}
                    size="small"
                    sx={{
                      backgroundColor: "#eee",
                      "&:hover": { backgroundColor: "#ddd" }
                    }}
                  >
                    <AddIcon />
                  </IconButton>

                  <IconButton
                    onClick={() => removeFromCart(item._id)}
                    color="error"
                    sx={{ ml: 2 }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          ))}

          <Divider sx={{ my: 3 }} />

          {/* Total y botones */}
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              backgroundColor: "#f5f5f5",
              padding: "16px",
              borderRadius: 2,
              boxShadow: 2
            }}
          >
            <Typography variant="h6" sx={{ fontWeight: "bold" }}>
              Total: S/ {total.toFixed(2)}
            </Typography>
            <Box sx={{ display: "flex", gap: 2 }}>
              <Button
                variant="outlined"
                color="error"
                onClick={clearCart}
                sx={{
                  padding: "10px 20px",
                  fontSize: "14px",
                  fontWeight: "bold",
                  borderRadius: "8px"
                }}
              >
                Vaciar carrito
              </Button>
              <Button
                variant="contained"
                color="primary"
                size="large"
                component={Link}
                to="/checkout"
                sx={{
                  padding: "10px 20px",
                  fontSize: "16px",
                  fontWeight: "bold",
                  borderRadius: "8px"
                }}
              >
                Proceder al pago
              </Button>
            </Box>
          </Box>
        </>
      )}
    </Container>
  );
}

export default Cart;
