// src/pages/Home.js
import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import {
  Container, Typography, Grid, Card, CardMedia, CardContent, CardActions,
  Button, CircularProgress, Snackbar, Alert, Box, List, ListItem,
  ListItemText, Paper, TextField, Badge
} from "@mui/material";
import { CartContext } from "../context/CartContext";
import { getImageUrl } from "../utils/imageUtils";
import { apiGet } from "../utils/api";

import { motion } from "framer-motion";
const MotionBox = motion(Box);
const MotionTypography = motion(Typography);


const CATEGORIES = [
  {
    name: "Productos",
    subcategories: [
      "Microcontroladores", "Arduino", "ESP32", "Raspberry",
      "Sensores", "Componentes", "ImpresiÃ³n 3D"
    ]
  },
  {
    name: "Industria",
    subcategories: [
      "MinerÃ­a", "PesquerÃ­a", "GanaderÃ­a", "ConstrucciÃ³n",
      "Militar", "Agricultura"
    ]
  },
  { name: "RobÃ³tica", subcategories: [] },
  { name: "Cursos", subcategories: [] }
];

const CARD_STYLES = {
  root: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    transition: "transform 0.2s ease, box-shadow 0.2s ease",
    "&:hover": { transform: "scale(1.03)", boxShadow: 6 }
  },
  media: {
    objectFit: "contain",
    bgcolor: "#f5f5f5",
    borderBottom: "1px solid #ddd",
    height: 200,
    p: 2
  },
  content: { flexGrow: 1, display: "flex", flexDirection: "column" },
  description: {
    mb: 1,
    minHeight: 40,
    overflow: "hidden",
    textOverflow: "ellipsis",
    display: "-webkit-box",
    WebkitLineClamp: 2,
    WebkitBoxOrient: "vertical"
  }
};

const CATEGORY_ITEM_STYLES = (isSelected) => ({
  width: "auto",
  mr: 2,
  mb: 1,
  "&:hover": { bgcolor: "grey.200" },
  bgcolor: isSelected ? "primary.main" : "transparent",
  color: isSelected ? "white" : "inherit",
  borderRadius: 1
});

function Home() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({ open: false, message: "" });
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Todos");
  const { addToCart } = useContext(CartContext);

  // Fetch products & normalize image URLs (via /api proxy)
  useEffect(() => {
    (async () => {
      try {
        const data = await apiGet("/products", { withAuth: false });
        const normalized = (Array.isArray(data) ? data : []).map((p) => ({
          ...p,
          _id: p._id ?? p.id,
          image: getImageUrl(p.image), // normalizamos 1 sola vez
          description: p.description || "",
          category: p.category || "",
          name: p.name || "",
        }));
        setProducts(normalized);
        setFilteredProducts(normalized);
      } catch (err) {
        console.error("Error al obtener productos:", err);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // Filter products
  useEffect(() => {
    const term = search.trim().toLowerCase();
    const cat = selectedCategory.toLowerCase();

    const next = products.filter((p) => {
      const matchesCat =
        selectedCategory === "Todos" ||
        (p.category && p.category.toLowerCase() === cat);
      const matchesSearch =
        term === "" ||
        p.name.toLowerCase().includes(term) ||
        (p.description || "").toLowerCase().includes(term);
      return matchesCat && matchesSearch;
    });

    setFilteredProducts(next);
  }, [search, selectedCategory, products]);

  const getCategoryCount = (category) => {
    if (category === "Todos") return products.length;
    const cat = category.toLowerCase();
    return products.filter((p) => p.category && p.category.toLowerCase() === cat).length;
    };

  const handleAddToCart = (product) => {
    addToCart(product);
    setSnackbar({
      open: true,
      message: `${product.name} agregado al carrito ðŸ›’`
    });
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: "center", mt: 10 }}>
        <CircularProgress />
        <Typography variant="h6" sx={{ mt: 2 }}>Cargando productos...</Typography>
      </Container>
    );
  }

  return (
    <>
    {/* Hero */}
    <MotionBox
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      sx={{
        width: "100%",
        height: { xs: 225, md: 250 },
        backgroundImage: "url('/images/titilab-banner.png')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "#fff",
        textAlign: "center",
        mb: 4,
        position: "relative"
      }}
    >
      {/* Overlay */}
      <Box
        sx={{
          position: "absolute",
          inset: 0,
          backgroundColor: "rgba(0,0,0,0.4)",
          zIndex: 1
        }}
      />

      {/* Animated Text */}
      <MotionTypography
        variant="h4"
        fontWeight="bold"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 1 }}
        sx={{
          position: "relative",
          zIndex: 2,
          bgcolor: "rgba(0,0,0,0.5)",
          p: 2,
          borderRadius: 2,
          maxWidth: "90%"
        }}
      >
        Bienvenido a Titilab Store â€“ Componentes y Soluciones IoT
      </MotionTypography>
    </MotionBox>

      <Container maxWidth="xl">
        {/* Search & Categories */}
        <Box sx={{ mb: 3 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Buscar productos..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            sx={{ mb: 2 }}
          />
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" fontWeight="bold" gutterBottom>
              CategorÃ­as
            </Typography>
            <List sx={{ display: "flex", flexWrap: "wrap" }}>
              <ListItem
                button
                selected={selectedCategory === "Todos"}
                onClick={() => setSelectedCategory("Todos")}
                sx={CATEGORY_ITEM_STYLES(selectedCategory === "Todos")}
              >
                <ListItemText primary="Todos" />
                <Badge
                  badgeContent={getCategoryCount("Todos")}
                  color={selectedCategory === "Todos" ? "secondary" : "primary"}
                  sx={{ ml: 1 }}
                />
              </ListItem>
              {CATEGORIES.map((group, idx) => (
                <Box key={idx} sx={{ display: "flex", flexWrap: "wrap" }}>
                  {group.subcategories.length > 0
                    ? group.subcategories.map((sub, i) => (
                        <ListItem
                          button
                          key={i}
                          selected={selectedCategory === sub}
                          onClick={() => setSelectedCategory(sub)}
                          sx={CATEGORY_ITEM_STYLES(selectedCategory === sub)}
                        >
                          <ListItemText primary={sub} />
                          <Badge
                            badgeContent={getCategoryCount(sub)}
                            color={selectedCategory === sub ? "secondary" : "primary"}
                            sx={{ ml: 1 }}
                          />
                        </ListItem>
                      ))
                    : (
                        <ListItem
                          button
                          selected={selectedCategory === group.name}
                          onClick={() => setSelectedCategory(group.name)}
                          sx={CATEGORY_ITEM_STYLES(selectedCategory === group.name)}
                        >
                          <ListItemText primary={group.name} />
                          <Badge
                            badgeContent={getCategoryCount(group.name)}
                            color={selectedCategory === group.name ? "secondary" : "primary"}
                            sx={{ ml: 1 }}
                          />
                        </ListItem>
                      )}
                </Box>
              ))}
            </List>
          </Paper>
        </Box>

        {/* Products */}
        <Box sx={{ mb: 4 }}>
          <Typography variant="h5" gutterBottom fontWeight="bold">
            {selectedCategory === "Todos" ? "Todos los Productos" : selectedCategory}
          </Typography>
          <Grid container spacing={3}>
            {filteredProducts.length > 0 ? (
              filteredProducts.map((product) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={product._id}>
                  <Card sx={CARD_STYLES.root}>
                    <Link
                      to={`/producto/${product._id}`}
                      style={{ textDecoration: "none", color: "inherit" }}
                    >
                      <CardMedia
                        component="img"
                        image={product.image}
                        alt={product.name}
                        sx={CARD_STYLES.media}
                      />
                    </Link>
                    <CardContent sx={CARD_STYLES.content}>
                      <Typography
                        variant="subtitle1"
                        fontWeight="bold"
                        noWrap
                        component={Link}
                        to={`/producto/${product._id}`}
                        style={{ textDecoration: "none", color: "inherit" }}
                      >
                        {product.name}
                      </Typography>
                      <Typography
                        variant="body2"
                        color="text.secondary"
                        sx={CARD_STYLES.description}
                      >
                        {product.description}
                      </Typography>
                      <Typography variant="h6" color="primary" sx={{ mt: "auto" }}>
                        S/ {Number(product.price || 0).toFixed(2)}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button
                        variant="contained"
                        size="small"
                        fullWidth
                        onClick={() => handleAddToCart(product)}
                      >
                        Agregar al carrito
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))
            ) : (
              <Grid item xs={12}>
                <Typography variant="body1" color="text.secondary">
                  No se encontraron productos para esta bÃºsqueda.
                </Typography>
              </Grid>
            )}
          </Grid>
        </Box>

        {/* Snackbar */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={2000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          anchorOrigin={{ vertical: "top", horizontal: "center" }}
        >
          <Alert severity="success" sx={{ width: "100%" }}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Container>
    </>
  );
}

export default Home;
