import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  IconButton,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Divider,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:4000";

function Admin() {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    stock: "",
    image: null,
  });

  // ‚úÖ Verificar login y cargar datos
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/admin-login";
      return;
    }
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      if (payload.exp * 1000 < Date.now()) {
        localStorage.removeItem("token");
        window.location.href = "/admin-login";
        return;
      }
    } catch {
      localStorage.removeItem("token");
      window.location.href = "/admin-login";
      return;
    }
    fetchProducts();
    fetchOrders();
  }, []);

  // üìå Obtener productos
  const fetchProducts = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) setProducts(await res.json());
  };

  // üìå Obtener pedidos
  const fetchOrders = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/orders`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) setOrders(await res.json());
  };

  // üìå Manejo de inputs productos
  const handleChange = (e) => {
    if (e.target.name === "image") {
      setFormData({ ...formData, image: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // üìå Agregar producto
  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.keys(formData).forEach((key) => {
      form.append(key, formData[key]);
    });

    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: form,
    });

    if (res.ok) {
      alert("‚úÖ Producto agregado");
      setFormData({
        name: "",
        description: "",
        category: "",
        price: "",
        stock: "",
        image: null,
      });
      fetchProducts();
    } else {
      alert("‚ùå Error al agregar producto");
    }
  };

  // üìå Eliminar producto
  const deleteProduct = async (id) => {
    if (!window.confirm("¬øEliminar producto?")) return;
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) {
      alert("üóëÔ∏è Producto eliminado");
      setProducts(products.filter((p) => p._id !== id));
    }
  };

  // üìå Cambiar estado pedido
  const updateOrderStatus = async (id, status) => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/orders/${id}/status`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ status }),
    });
    if (res.ok) {
      fetchOrders();
    }
  };

  // üìå Cerrar sesi√≥n
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/admin-login";
  };

  // üìå Filtrar pedidos por estado
  const filterOrders = (estado) =>
    orders.filter((o) => (o.status || "pendiente") === estado);

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        üì¶ Panel de Administraci√≥n
      </Typography>
      <Button
        variant="outlined"
        color="secondary"
        onClick={handleLogout}
        sx={{ mb: 2 }}
      >
        Cerrar Sesi√≥n
      </Button>

      {/* === FORMULARIO PRODUCTOS === */}
      <form onSubmit={handleSubmit} style={{ marginBottom: "30px" }}>
        <Typography variant="h5">‚ûï Agregar Producto</Typography>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Nombre"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Categor√≠a"
              name="category"
              value={formData.category}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Precio"
              name="price"
              type="number"
              value={formData.price}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Stock"
              name="stock"
              type="number"
              value={formData.stock}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={8}>
            <TextField
              fullWidth
              label="Descripci√≥n"
              name="description"
              multiline
              rows={2}
              value={formData.description}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <Button variant="outlined" component="label" fullWidth>
              Subir Imagen
              <input type="file" name="image" hidden onChange={handleChange} />
            </Button>
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" startIcon={<AddIcon />}>
              Agregar Producto
            </Button>
          </Grid>
        </Grid>
      </form>

      {/* === LISTA PRODUCTOS === */}
      <Typography variant="h5" gutterBottom>
        üõí Lista de Productos
      </Typography>
      <Grid container spacing={3}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product._id}>
            <Card>
              {product.image && (
                <CardMedia
                  component="img"
                  height="140"
                  image={product.image}
                  alt={product.name}
                />
              )}
              <CardContent>
                <Typography variant="h6">{product.name}</Typography>
                <Typography variant="body2">{product.description}</Typography>
                <Typography variant="body1" sx={{ mt: 1 }}>
                  ${product.price} - Stock: {product.stock}
                </Typography>
              </CardContent>
              <CardActions>
                <IconButton
                  color="error"
                  onClick={() => deleteProduct(product._id)}
                >
                  <DeleteIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Divider sx={{ my: 4 }} />

      {/* === LISTA PEDIDOS === */}
      {["pendiente", "enviado", "completado"].map((estado) => (
        <div key={estado}>
          <Typography variant="h5" sx={{ mt: 3 }}>
            {estado === "pendiente" && "üìå Pendientes"}
            {estado === "enviado" && "üì¶ Enviados"}
            {estado === "completado" && "‚úÖ Completados"}
          </Typography>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            {filterOrders(estado).map((order) => (
              <Grid item xs={12} key={order.id}>
                <Card>
                  <CardContent>
                    <Typography variant="subtitle1">
                      <strong>Pedido #{order.id}</strong> - {order.nombre} ({order.email})
                    </Typography>
                    <Typography variant="body2">
                      Direcci√≥n: {order.direccion} - Tel: {order.telefono}
                    </Typography>
                    <Typography variant="body2">
                      Total: S/. {order.total}
                    </Typography>
                    <Typography variant="body2" sx={{ mt: 1 }}>
                      üõç Productos:
                    </Typography>
                    <ul>
                      {order.cart.map((item, idx) => (
                        <li key={idx}>
                          {item.name} (x{item.quantity}) - S/. {item.price}
                        </li>
                      ))}
                    </ul>
                    <FormControl size="small" sx={{ mt: 1 }}>
                      <InputLabel>Estado</InputLabel>
                      <Select
                        value={order.status || "pendiente"}
                        label="Estado"
                        onChange={(e) =>
                          updateOrderStatus(order.id, e.target.value)
                        }
                      >
                        <MenuItem value="pendiente">Pendiente</MenuItem>
                        <MenuItem value="enviado">Enviado</MenuItem>
                        <MenuItem value="completado">Completado</MenuItem>
                      </Select>
                    </FormControl>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </div>
      ))}
    </Container>
  );
}

export default Admin;
