import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import {
  AppBar, Toolbar, Typography, Button, IconButton, Menu, MenuItem, Badge, Grow, Paper
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import AdminPanelSettingsIcon from "@mui/icons-material/AdminPanelSettings";
import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";

function Navbar() {
  const location = useLocation();
  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorProductos, setAnchorProductos] = useState(null);
  const [anchorIndustria, setAnchorIndustria] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  // Verificar admin
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split(".")[1]));
        if (payload.role === "admin") setIsAdmin(true);
      } catch {
        setIsAdmin(false);
      }
    }
  }, []);

  // Contador carrito
  useEffect(() => {
    const cart = JSON.parse(localStorage.getItem("cart") || "[]");
    setCartCount(cart.reduce((acc, item) => acc + (item.quantity || 1), 0));
  }, [location]);

  const linkStyle = (path) => ({
    color: "#fff",
    marginRight: "15px",
    textDecoration: location.pathname === path ? "underline" : "none"
  });

  // Estilo de menÃº con sombra y bordes
  const dropdownStyle = {
    boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
    borderRadius: "8px",
    padding: "4px 0",
    minWidth: "200px"
  };

  const menuItemStyle = {
    transition: "background-color 0.2s ease",
    "&:hover": {
      backgroundColor: "#f0f0f0"
    }
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: "#1e1e1e" }}>
      <Toolbar>
        {/* Logo */}
        <Typography
          variant="h6"
          component={Link}
          to="/"
          sx={{
            flexGrow: 1,
            color: "#fff",
            textDecoration: "none",
            fontWeight: "bold",
            fontSize: "1.3rem"
          }}
        >
          ð Titilab
        </Typography>

        {/* MenÃº mÃ³vil */}
        <IconButton
          edge="start"
          color="inherit"
          sx={{ display: { xs: "flex", md: "none" } }}
          onClick={(e) => setAnchorEl(e.currentTarget)}
        >
          <MenuIcon />
        </IconButton>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={() => setAnchorEl(null)}
        >
          <MenuItem component={Link} to="/productos/microcontroladores">Microcontroladores</MenuItem>
          <MenuItem component={Link} to="/productos/arduino">Arduino</MenuItem>
          <MenuItem component={Link} to="/productos/esp32">ESP32</MenuItem>
          <MenuItem component={Link} to="/productos/raspberry">Raspberry</MenuItem>
          <MenuItem component={Link} to="/productos/sensores">Sensores</MenuItem>
          <MenuItem component={Link} to="/productos/componentes">Componentes</MenuItem>

          <MenuItem component={Link} to="/industria/mineria">MinerÃ­a</MenuItem>
          <MenuItem component={Link} to="/industria/pesqueria">PesquerÃ­a</MenuItem>
          <MenuItem component={Link} to="/industria/ganaderia">GanaderÃ­a</MenuItem>
          <MenuItem component={Link} to="/industria/construccion">ConstrucciÃ³n</MenuItem>

          <MenuItem component={Link} to="/impresion3d">ImpresiÃ³n 3D</MenuItem>
          <MenuItem component={Link} to="/robotica">RobÃ³tica</MenuItem>
          <MenuItem component={Link} to="/cursos">Cursos</MenuItem>
          <MenuItem component={Link} to="/cart">
            Carrito <Badge badgeContent={cartCount} color="secondary" sx={{ ml: 1 }} />
          </MenuItem>
          {isAdmin && <MenuItem component={Link} to="/admin">Admin</MenuItem>}
        </Menu>

        {/* MenÃº escritorio */}
        <div style={{ display: "flex", alignItems: "center" }}>
          {/* Productos */}
          <div
            onMouseEnter={(e) => setAnchorProductos(e.currentTarget)}
            onMouseLeave={() => setAnchorProductos(null)}
          >
            <Button
              sx={{ ...linkStyle("/productos"), display: { xs: "none", md: "inline-flex" } }}
              endIcon={<ArrowDropDownIcon />}
            >
              Productos
            </Button>
            <Menu
              anchorEl={anchorProductos}
              open={Boolean(anchorProductos)}
              onClose={() => setAnchorProductos(null)}
              TransitionComponent={Grow}
              PaperProps={{ sx: dropdownStyle }}
              MenuListProps={{
                onMouseEnter: () => setAnchorProductos(anchorProductos),
                onMouseLeave: () => setAnchorProductos(null)
              }}
            >
              <MenuItem sx={menuItemStyle} component={Link} to="/productos/microcontroladores">Microcontroladores</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/productos/arduino">Arduino</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/productos/esp32">ESP32</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/productos/raspberry">Raspberry</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/productos/sensores">Sensores</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/productos/componentes">Componentes</MenuItem>
            </Menu>
          </div>

          {/* Industria */}
          <div
            onMouseEnter={(e) => setAnchorIndustria(e.currentTarget)}
            onMouseLeave={() => setAnchorIndustria(null)}
          >
            <Button
              sx={{ ...linkStyle("/industria"), display: { xs: "none", md: "inline-flex" } }}
              endIcon={<ArrowDropDownIcon />}
            >
              Industria
            </Button>
            <Menu
              anchorEl={anchorIndustria}
              open={Boolean(anchorIndustria)}
              onClose={() => setAnchorIndustria(null)}
              TransitionComponent={Grow}
              PaperProps={{ sx: dropdownStyle }}
              MenuListProps={{
                onMouseEnter: () => setAnchorIndustria(anchorIndustria),
                onMouseLeave: () => setAnchorIndustria(null)
              }}
            >
              <MenuItem sx={menuItemStyle} component={Link} to="/industria/mineria">MinerÃ­a</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/industria/pesqueria">PesquerÃ­a</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/industria/ganaderia">GanaderÃ­a</MenuItem>
              <MenuItem sx={menuItemStyle} component={Link} to="/industria/construccion">ConstrucciÃ³n</MenuItem>
            </Menu>
          </div>

          <Button
            component={Link}
            to="/impresion3d"
            sx={{ ...linkStyle("/impresion3d"), display: { xs: "none", md: "inline-flex" } }}
          >
            ImpresiÃ³n 3D
          </Button>
          <Button
            component={Link}
            to="/robotica"
            sx={{ ...linkStyle("/robotica"), display: { xs: "none", md: "inline-flex" } }}
          >
            RobÃ³tica
          </Button>
          <Button
            component={Link}
            to="/cursos"
            sx={{ ...linkStyle("/cursos"), display: { xs: "none", md: "inline-flex" } }}
          >
            Cursos
          </Button>

          <IconButton
            component={Link}
            to="/cart"
            sx={{ color: "white", display: { xs: "none", md: "inline-flex" } }}
          >
            <Badge badgeContent={cartCount} color="secondary">
              <ShoppingCartIcon />
            </Badge>
          </IconButton>

          {isAdmin && (
            <Button
              component={Link}
              to="/admin"
              sx={{ ...linkStyle("/admin"), display: { xs: "none", md: "inline-flex" } }}
              startIcon={<AdminPanelSettingsIcon />}
            >
              Admin
            </Button>
          )}
        </div>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
