import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import {
  Container,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  CircularProgress,
  Snackbar,
  Alert,
  Box,
  TextField,
  Badge,
  Paper,
  List,
  ListItem,
  ListItemText
} from "@mui/material";
import { CartContext } from "../context/CartContext";

function Home() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({ open: false, message: "" });
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Todos");
  const { addToCart } = useContext(CartContext);

  const categoryStructure = [
    {
      name: "Productos",
      subcategories: [
        "Microcontroladores",
        "Arduino",
        "ESP32",
        "Raspberry",
        "Sensores",
        "Componentes"
      ]
    },
    {
      name: "Industria",
      subcategories: [
        "MinerÃ­a",
        "PesquerÃ­a",
        "GanaderÃ­a",
        "ConstrucciÃ³n",
        "Militar",
        "Agricultura"
      ]
    },
    { name: "ImpresiÃ³n 3D", subcategories: [] },
    { name: "RobÃ³tica", subcategories: [] },
    { name: "Cursos", subcategories: [] }
  ];

  useEffect(() => {
    axios
      .get("http://localhost:4000/api/products")
      .then((res) => {
        setProducts(res.data);
        setFilteredProducts(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error al obtener productos:", err);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    let tempProducts = [...products];

    if (selectedCategory !== "Todos") {
      tempProducts = tempProducts.filter(
        (p) => p.category && p.category.toLowerCase() === selectedCategory.toLowerCase()
      );
    }

    if (search.trim() !== "") {
      tempProducts = tempProducts.filter(
        (p) =>
          p.name.toLowerCase().includes(search.toLowerCase()) ||
          p.description.toLowerCase().includes(search.toLowerCase())
      );
    }

    setFilteredProducts(tempProducts);
  }, [search, selectedCategory, products]);

  const getCategoryCount = (category) => {
    if (category === "Todos") return products.length;
    return products.filter(
      (p) => p.category && p.category.toLowerCase() === category.toLowerCase()
    ).length;
  };

  const handleAddToCart = (product) => {
    addToCart(product);
    setSnackbar({
      open: true,
      message: `${product.name} agregado al carrito ðŸ›’`
    });
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: "center", mt: 10 }}>
        <CircularProgress />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Cargando productos...
        </Typography>
      </Container>
    );
  }

  return (
    <>
      {/* Hero Banner */}
      <Box
        sx={{
          width: "100%",
          height: { xs: 80, md: 140 },
          backgroundImage: "url('https://via.placeholder.com/1600x400?text=Titilab+Store')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "#fff",
          textAlign: "center",
          mb: 4
        }}
      >
        <Typography
          variant="h4"
          fontWeight="bold"
          sx={{
            bgcolor: "rgba(0,0,0,0.5)",
            p: 2,
            borderRadius: 2,
            maxWidth: "90%"
          }}
        >
          Bienvenido a Titilab Store â€“ Componentes y Soluciones IoT
        </Typography>
      </Box>

      <Container maxWidth="xl">
        {/* Search bar */}
        <Box sx={{ mb: 3 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Buscar productos..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </Box>

        {/* Categories */}
        <Paper sx={{ p: 2, mb: 3 }}>
          <Typography variant="h6" fontWeight="bold" gutterBottom>
            CategorÃ­as
          </Typography>
          <List sx={{ display: "flex", flexWrap: "wrap", gap: 1 }}>
            <ListItem
              button
              selected={selectedCategory === "Todos"}
              onClick={() => setSelectedCategory("Todos")}
              sx={{
                border: "1px solid",
                borderColor: selectedCategory === "Todos" ? "primary.main" : "grey.300",
                borderRadius: 1,
                bgcolor: selectedCategory === "Todos" ? "primary.main" : "transparent",
                color: selectedCategory === "Todos" ? "white" : "inherit",
                px: 2,
                py: 0.5
              }}
            >
              <ListItemText primary="Todos" />
              <Badge
                badgeContent={getCategoryCount("Todos")}
                color={selectedCategory === "Todos" ? "secondary" : "primary"}
                sx={{ ml: 1 }}
              />
            </ListItem>

            {categoryStructure.map((group, idx) => (
              <React.Fragment key={idx}>
                {group.subcategories.length > 0
                  ? group.subcategories.map((sub, i) => (
                      <ListItem
                        button
                        key={i}
                        selected={selectedCategory === sub}
                        onClick={() => setSelectedCategory(sub)}
                        sx={{
                          border: "1px solid",
                          borderColor: selectedCategory === sub ? "primary.main" : "grey.300",
                          borderRadius: 1,
                          bgcolor: selectedCategory === sub ? "primary.main" : "transparent",
                          color: selectedCategory === sub ? "white" : "inherit",
                          px: 2,
                          py: 0.5
                        }}
                      >
                        <ListItemText primary={sub} />
                        <Badge
                          badgeContent={getCategoryCount(sub)}
                          color={selectedCategory === sub ? "secondary" : "primary"}
                          sx={{ ml: 1 }}
                        />
                      </ListItem>
                    ))
                  : (
                      <ListItem
                        button
                        selected={selectedCategory === group.name}
                        onClick={() => setSelectedCategory(group.name)}
                        sx={{
                          border: "1px solid",
                          borderColor:
                            selectedCategory === group.name ? "primary.main" : "grey.300",
                          borderRadius: 1,
                          bgcolor: selectedCategory === group.name ? "primary.main" : "transparent",
                          color: selectedCategory === group.name ? "white" : "inherit",
                          px: 2,
                          py: 0.5
                        }}
                      >
                        <ListItemText primary={group.name} />
                        <Badge
                          badgeContent={getCategoryCount(group.name)}
                          color={selectedCategory === group.name ? "secondary" : "primary"}
                          sx={{ ml: 1 }}
                        />
                      </ListItem>
                    )}
              </React.Fragment>
            ))}
          </List>
        </Paper>

        {/* Products Section */}
        <Typography variant="h5" gutterBottom fontWeight="bold">
          {selectedCategory === "Todos" ? "Todos los Productos" : selectedCategory}
        </Typography>
        <Grid container spacing={3}>
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={product._id}>
                <Card
                  sx={{
                    height: 350,
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "space-between",
                    transition: "transform 0.2s ease, box-shadow 0.2s ease",
                    "&:hover": {
                      transform: "scale(1.03)",
                      boxShadow: 6
                    }
                  }}
                >
                  <Link
                    to={`/producto/${product._id}`}
                    style={{ textDecoration: "none", color: "inherit" }}
                  >
                    <CardMedia
                      component="img"
                      height="180"
                      image={product.image}
                      alt={product.name}
                      sx={{
                        objectFit: "contain",
                        bgcolor: "#f5f5f5",
                        borderBottom: "1px solid #ddd"
                      }}
                    />
                  </Link>

                  <CardContent
                    sx={{
                      flexGrow: 1,
                      display: "flex",
                      flexDirection: "column",
                      justifyContent: "space-between"
                    }}
                  >
                    <Typography
                      variant="subtitle1"
                      fontWeight="bold"
                      noWrap
                      component={Link}
                      to={`/producto/${product._id}`}
                      style={{ textDecoration: "none", color: "inherit" }}
                    >
                      {product.name}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="text.secondary"
                      sx={{
                        mb: 1,
                        minHeight: 40,
                        overflow: "hidden",
                        textOverflow: "ellipsis",
                        display: "-webkit-box",
                        WebkitLineClamp: 2,
                        WebkitBoxOrient: "vertical"
                      }}
                    >
                      {product.description}
                    </Typography>
                    <Typography variant="h6" color="primary">
                      S/ {Number(product.price).toFixed(2)}
                    </Typography>
                  </CardContent>
                  <CardActions sx={{ mt: "auto" }}>
                    <Button
                      variant="contained"
                      size="small"
                      fullWidth
                      onClick={() => handleAddToCart(product)}
                    >
                      Agregar al carrito
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))
          ) : (
            <Grid item xs={12}>
              <Typography variant="body1" color="text.secondary">
                No se encontraron productos para esta bÃºsqueda.
              </Typography>
            </Grid>
          )}
        </Grid>

        {/* Snackbar */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={2000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          anchorOrigin={{ vertical: "top", horizontal: "center" }}
        >
          <Alert severity="success" sx={{ width: "100%" }}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Container>
    </>
  );
}

export default Home;
