import React, { useState, useEffect } from "react";
import {
  Container, Typography, TextField, Button, Grid, IconButton, MenuItem, Select,
  FormControl, Divider, List, ListItem, ListItemText, ListItemSecondaryAction,
  Card, CardMedia
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";
import PhotoCameraIcon from "@mui/icons-material/PhotoCamera";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:4000";

export default function Admin() {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [imagePreview, setImagePreview] = useState(null);
  const [formData, setFormData] = useState({
    name: "", description: "", category: "", price: "", stock: "", image: null
  });

  const categoryOptions = [
    "Robótica","Impresión 3D","Minería","Pesquería","Ganadería",
    "Construcción","Militar","Microcontroladores","Arduino",
    "ESP32","Raspberry","Sensores","Componentes"
  ];

  const getToken = () => localStorage.getItem("token");

  const apiFetch = async (endpoint, options = {}) => {
    const token = getToken();
    return fetch(`${BACKEND_URL}${endpoint}`, {
      headers: { Authorization: `Bearer ${token}`, ...options.headers },
      ...options
    });
  };

  const validateToken = () => {
    const token = getToken();
    if (!token) return false;
    try {
      const { exp } = JSON.parse(atob(token.split(".")[1]));
      if (exp * 1000 < Date.now()) return false;
    } catch {
      return false;
    }
    return true;
  };

  useEffect(() => {
    if (!validateToken()) {
      localStorage.removeItem("token");
      window.location.href = "/admin-login";
      return;
    }
    fetchProducts();
    fetchOrders();
  }, []);

  const fetchProducts = async () => {
    const res = await apiFetch("/api/products");
    if (res.ok) setProducts(await res.json());
  };

  const fetchOrders = async () => {
    const res = await apiFetch("/api/orders");
    if (res.ok) setOrders(await res.json());
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image" && files?.length) {
      const file = files[0];
      setFormData((prev) => ({ ...prev, image: file }));
      setImagePreview(URL.createObjectURL(file));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.entries(formData).forEach(([k, v]) => form.append(k, v));

    const res = await apiFetch("/api/products", { method: "POST", body: form });
    if (res.ok) {
      alert("✅ Producto agregado");
      setFormData({ name: "", description: "", category: "", price: "", stock: "", image: null });
      setImagePreview(null);
      fetchProducts();
    } else {
      alert("❌ Error al agregar producto");
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("¿Eliminar producto?")) return;
    const res = await apiFetch(`/api/products/${id}`, { method: "DELETE" });
    if (res.ok) {
      alert("🗑️ Producto eliminado");
      setProducts((prev) => prev.filter((p) => p._id !== id));
    }
  };

  const updateOrderStatus = async (id, status) => {
    const res = await apiFetch(`/api/orders/${id}/status`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ status })
    });
    if (res.ok) fetchOrders();
  };

  const filterOrders = (estado) => orders.filter((o) => (o.status || "pendiente") === estado);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/admin-login";
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>📦 Panel de Administración</Typography>
      <Button variant="outlined" color="secondary" onClick={handleLogout} sx={{ mb: 2 }}>Cerrar Sesión</Button>

      {/* === FORMULARIO === */}
      <Card sx={{ p: 3, mb: 4, borderRadius: 3, boxShadow: 3 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>➕ Agregar Producto</Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {[
              { label: "Nombre del producto", name: "name", xs: 12, sm: 6, md: 4 },
              { label: "Precio (S/.)", name: "price", type: "number", xs: 6, sm: 3, md: 2 },
              { label: "Stock", name: "stock", type: "number", xs: 6, sm: 3, md: 2 }
            ].map((f) => (
              <Grid key={f.name} item xs={f.xs} sm={f.sm} md={f.md}>
                <TextField {...f} fullWidth value={formData[f.name]} onChange={handleChange} required />
              </Grid>
            ))}
            <Grid item xs={12} sm={6} md={4}>
              <TextField select fullWidth label="Categoría" name="category" value={formData.category} onChange={handleChange} required>
                {categoryOptions.map((cat) => <MenuItem key={cat} value={cat}>{cat}</MenuItem>)}
              </TextField>
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="Descripción" name="description" multiline rows={3} value={formData.description} onChange={handleChange} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <Button variant="outlined" component="label" startIcon={<PhotoCameraIcon />} fullWidth>
                Subir Imagen
                <input type="file" name="image" accept="image/*" hidden onChange={handleChange} />
              </Button>
            </Grid>
            <Grid item xs={12} sm={6}>
              {imagePreview && <CardMedia component="img" height="150" image={imagePreview} alt="Vista previa" sx={{ borderRadius: 2 }} />}
            </Grid>
            <Grid item xs={12}>
              <Button type="submit" variant="contained" startIcon={<AddIcon />} fullWidth sx={{ py: 1.5, fontWeight: "bold" }}>
                Agregar Producto
              </Button>
            </Grid>
          </Grid>
        </form>
      </Card>

      <Divider sx={{ my: 3 }} />

      {/* === PRODUCTOS === */}
      <Typography variant="h5" gutterBottom>🛒 Lista de Productos</Typography>
      <List>
        {products.map((p) => (
          <ListItem key={p._id}>
            <ListItemText primary={`${p.name} (${p.category})`} secondary={`S/. ${p.price} | Stock: ${p.stock}`} />
            <ListItemSecondaryAction>
              <IconButton edge="end" color="error" onClick={() => deleteProduct(p._id)}>
                <DeleteIcon />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>

      <Divider sx={{ my: 4 }} />

      {/* === PEDIDOS === */}
      {["pendiente", "enviado", "completado"].map((estado) => (
        <div key={estado}>
          <Typography variant="h5" sx={{ mt: 3 }}>
            {estado === "pendiente" && "📌 Pendientes"}
            {estado === "enviado" && "📦 Enviados"}
            {estado === "completado" && "✅ Completados"}
          </Typography>
          <List>
            {filterOrders(estado).map((o) => (
              <ListItem key={o.id} alignItems="flex-start">
                <ListItemText
                  primary={`Pedido #${o.id} - ${o.nombre} (${o.email})`}
                  secondary={
                    <>
                      Dirección: {o.direccion} - Tel: {o.telefono}<br />
                      Total: S/. {o.total}<br />
                      Productos: {o.cart.map((i) => `${i.name} (x${i.quantity})`).join(", ")}
                    </>
                  }
                />
                <FormControl size="small" sx={{ minWidth: 120 }}>
                  <Select value={o.status || "pendiente"} onChange={(e) => updateOrderStatus(o.id, e.target.value)}>
                    <MenuItem value="pendiente">Pendiente</MenuItem>
                    <MenuItem value="enviado">Enviado</MenuItem>
                    <MenuItem value="completado">Completado</MenuItem>
                  </Select>
                </FormControl>
              </ListItem>
            ))}
          </List>
        </div>
      ))}
    </Container>
  );
}
