// src/context/CartContext.js
import React, { createContext, useState, useEffect } from "react";

export const CartContext = createContext();

export function CartProvider({ children }) {
  const [cart, setCart] = useState([]);

  // ðŸ“Œ Cargar desde localStorage al iniciar
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  // ðŸ“Œ Guardar en localStorage cuando cambie el carrito
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // ðŸ“Œ Agregar producto
  const addToCart = (product) => {
    setCart((prevCart) => {
      // Asegurarnos de que tenga un _id en string
      const productId = String(product._id);

      const existingIndex = prevCart.findIndex(
        (item) => String(item._id) === productId
      );

      if (existingIndex !== -1) {
        // Producto ya existe, aumentar cantidad
        const updatedCart = [...prevCart];
        updatedCart[existingIndex] = {
          ...updatedCart[existingIndex],
          quantity: updatedCart[existingIndex].quantity + 1
        };
        return updatedCart;
      } else {
        // Producto nuevo, agregar con cantidad 1
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  // ðŸ“Œ Quitar un producto por _id
  const removeFromCart = (productId) => {
    setCart((prevCart) =>
      prevCart.filter((item) => String(item._id) !== String(productId))
    );
  };

  // ðŸ“Œ Vaciar carrito
  const clearCart = () => {
    setCart([]);
  };

  return (
    <CartContext.Provider
      value={{
        cart,
        addToCart,
        removeFromCart,
        clearCart
      }}
    >
      {children}
    </CartContext.Provider>
  );
}
