// src/pages/Checkout.js
import React, { useEffect, useState, useContext } from "react";
import {
  Container, Typography, Grid, TextField, Button,
  Card, CardContent, Divider, MenuItem, Stepper, Step, StepLabel,
  Radio, RadioGroup, FormControlLabel
} from "@mui/material";

import departamentos from "../data/ubigeo_peru_2016_departamentos.json";
import provincias from "../data/ubigeo_peru_2016_provincias.json";
import distritos from "../data/ubigeo_peru_2016_distritos.json";
import { CartContext } from "../context/CartContext";

const pasos = ["Carrito", "Datos", "Pago", "Confirmaci√≥n"];

function Checkout() {
  const { cart, clearCart } = useContext(CartContext);

  const [activeStep, setActiveStep] = useState(1);
  const [departamento, setDepartamento] = useState("");
  const [provincia, setProvincia] = useState("");
  const [distrito, setDistrito] = useState("");
  const [courier, setCourier] = useState("Olva");
  const [tipoComprobante, setTipoComprobante] = useState("boleta");
  const [metodoPago, setMetodoPago] = useState("transferencia");

  const [formData, setFormData] = useState(() => {
    return JSON.parse(localStorage.getItem("checkoutForm")) || {
      nombre: "",
      email: "",
      direccion: "",
      telefono: "",
      dni: "",
      ruc: "",
      empresa: ""
    };
  });

  // Guardar datos del formulario en localStorage para persistencia
  useEffect(() => {
    localStorage.setItem("checkoutForm", JSON.stringify(formData));
  }, [formData]);

  // Calcular totales
  const getSubtotal = () =>
    cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const getEnvio = () => (courier === "Olva" ? 15 : 20);
  const getTotal = () => (getSubtotal() + getEnvio()).toFixed(2);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const validarDNI = (dni) => /^\d{8}$/.test(dni);
  const validarRUC = (ruc) =>
    /^\d{11}$/.test(ruc) &&
    ["10", "15", "17", "20"].includes(ruc.substring(0, 2));

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones b√°sicas
    if (!formData.nombre || !formData.email || !formData.direccion) {
      return alert("‚ö†Ô∏è Por favor, completa todos los campos obligatorios.");
    }
    if (tipoComprobante === "boleta" && !validarDNI(formData.dni)) {
      return alert("‚ö†Ô∏è DNI inv√°lido (8 d√≠gitos requeridos).");
    }
    if (
      tipoComprobante === "factura" &&
      (!validarRUC(formData.ruc) || !formData.empresa)
    ) {
      return alert("‚ö†Ô∏è RUC inv√°lido o falta raz√≥n social.");
    }

    try {
      const response = await fetch("/api/checkout", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...formData,
          tipoComprobante,
          departamento,
          provincia,
          distrito,
          courier,
          metodoPago,
          cart,
          total: getTotal()
        })
      });

      if (response.ok) {
        alert("‚úÖ Pedido realizado con √©xito. Revisa tu email para el comprobante.");
        clearCart();
        localStorage.removeItem("checkoutForm");
        setActiveStep(3);
      } else {
        alert("‚ùå Hubo un problema al procesar tu pedido.");
      }
    } catch (error) {
      alert("‚ö†Ô∏è Error de conexi√≥n con el servidor.");
      console.error(error);
    }
  };

  // Filtrado din√°mico de provincias y distritos
  const provinciasFiltradas = provincias.filter(
    (prov) => prov.department_id === departamento
  );
  const distritosFiltrados = distritos.filter(
    (dist) => dist.province_id === provincia
  );

  if (cart.length === 0) {
    return (
      <Container sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="h5">üõí No hay productos en tu carrito</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ mt: 4, mb: 6 }}>
      {/* Barra de progreso */}
      <Stepper activeStep={activeStep} alternativeLabel>
        {pasos.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      <Grid container spacing={4} sx={{ mt: 2 }}>
        {/* Formulario */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>Datos de env√≠o</Typography>
          <form onSubmit={handleSubmit}>
            <TextField label="Nombre completo" name="nombre" value={formData.nombre} onChange={handleChange} fullWidth margin="normal" required />
            <TextField label="Correo electr√≥nico" type="email" name="email" value={formData.email} onChange={handleChange} fullWidth margin="normal" required />
            <TextField label="Direcci√≥n" name="direccion" value={formData.direccion} onChange={handleChange} fullWidth margin="normal" required />
            <TextField label="Tel√©fono" name="telefono" value={formData.telefono} onChange={handleChange} fullWidth margin="normal" required />

            {/* Tipo de comprobante */}
            <Typography variant="subtitle1" sx={{ mt: 2 }}>Tipo de Comprobante</Typography>
            <RadioGroup value={tipoComprobante} onChange={(e) => setTipoComprobante(e.target.value)}>
              <FormControlLabel value="boleta" control={<Radio />} label="Boleta" />
              <FormControlLabel value="factura" control={<Radio />} label="Factura" />
            </RadioGroup>

            {tipoComprobante === "boleta" && (
              <TextField label="DNI" name="dni" value={formData.dni} onChange={handleChange} fullWidth margin="normal" required />
            )}
            {tipoComprobante === "factura" && (
              <>
                <TextField label="RUC" name="ruc" value={formData.ruc} onChange={handleChange} fullWidth margin="normal" required />
                <TextField label="Raz√≥n Social" name="empresa" value={formData.empresa} onChange={handleChange} fullWidth margin="normal" required />
              </>
            )}

            {/* Ubigeo din√°mico */}
            <TextField select label="Departamento" value={departamento} onChange={(e) => { setDepartamento(e.target.value); setProvincia(""); setDistrito(""); }} fullWidth margin="normal" required>
              {departamentos.map(dep => (
                <MenuItem key={dep.id} value={dep.id}>{dep.name}</MenuItem>
              ))}
            </TextField>

            <TextField select label="Provincia" value={provincia} onChange={(e) => { setProvincia(e.target.value); setDistrito(""); }} fullWidth margin="normal" required disabled={!departamento}>
              {provinciasFiltradas.map(prov => (
                <MenuItem key={prov.id} value={prov.id}>{prov.name}</MenuItem>
              ))}
            </TextField>

            <TextField select label="Distrito" value={distrito} onChange={(e) => setDistrito(e.target.value)} fullWidth margin="normal" required disabled={!provincia}>
              {distritosFiltrados.map(dist => (
                <MenuItem key={dist.id} value={dist.id}>{dist.name}</MenuItem>
              ))}
            </TextField>

            {/* Courier */}
            <Typography variant="subtitle1" sx={{ mt: 2 }}>M√©todo de env√≠o</Typography>
            <RadioGroup value={courier} onChange={(e) => setCourier(e.target.value)}>
              <FormControlLabel value="Olva" control={<Radio />} label="Olva Courier - S/15.00" />
              <FormControlLabel value="Shalom" control={<Radio />} label="Shalom - S/20.00" />
            </RadioGroup>

            {/* M√©todos de pago */}
            <Typography variant="subtitle1" sx={{ mt: 2 }}>M√©todo de pago</Typography>
            <RadioGroup value={metodoPago} onChange={(e) => setMetodoPago(e.target.value)}>
              <FormControlLabel value="transferencia" control={<Radio />} label="Transferencia bancaria" />
              <FormControlLabel value="culqi" control={<Radio />} label="Tarjeta de cr√©dito/d√©bito (Culqi)" />
            </RadioGroup>

            <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>Realizar Pedido</Button>
          </form>
        </Grid>

        {/* Resumen */}
        <Grid item xs={12} md={6} sx={{ position: "sticky", top: "20px", alignSelf: "flex-start" }}>
          <Typography variant="h6" gutterBottom>Resumen de compra</Typography>
          <Card>
            <CardContent>
              {cart.map(item => (
                <div key={item.cartId} style={{ marginBottom: "10px" }}>
                  <Typography variant="body1">{item.name} x {item.quantity}</Typography>
                  <Typography variant="body2" color="text.secondary">S/ {(item.price * item.quantity).toFixed(2)}</Typography>
                  <Divider sx={{ my: 1 }} />
                </div>
              ))}
              <Typography variant="body1">Subtotal: S/ {getSubtotal().toFixed(2)}</Typography>
              <Typography variant="body1">Env√≠o: S/ {getEnvio().toFixed(2)}</Typography>
              <Typography variant="h6" align="right">Total: S/ {getTotal()}</Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Checkout;
