            <Button
              sx={{ ...linkStyle("/industria"), display: { xs: "none", md: "inline-flex" } }}
              endIcon={<ArrowDropDownIcon />}
            >
              Industria
            </Button>
            <Menu
              anchorEl={anchorIndustria}
              open={Boolean(anchorIndustria)}
              onClose={() => setAnchorIndustria(null)}
