          {/* Admin */}
          {isAdmin && (
            <Button
              component={Link}
              to="/admin"
              sx={{ ...linkStyle("/admin"), display: { xs: "none", md: "inline-flex" } }}
              startIcon={<AdminPanelSettingsIcon />}
            >
              Admin
            </Button>
          )}
        </div>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
