import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  TextField,
  Button,
  Card,
  CardContent,
  Divider,
  RadioGroup,
  FormControlLabel,
  Radio
} from "@mui/material";

function Checkout() {
  const [cart, setCart] = useState([]);
  const [shipping, setShipping] = useState(15); // Valor inicial Olva
  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    direccion: "",
    telefono: "",
    ruc: "",
    empresa: "",
    envio: "Olva" // OpciÃ³n inicial
  });

  // ðŸ“Œ Cargar carrito
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  // ðŸ“Œ Calcular subtotal
  const getSubtotal = () =>
    cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  // ðŸ“Œ Calcular total
  const getTotal = () => (getSubtotal() + shipping).toFixed(2);

  // ðŸ“Œ Manejar cambios en formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));

    // Cambiar costo de envÃ­o segÃºn opciÃ³n
    if (name === "envio") {
      if (value === "Olva") setShipping(15);
      if (value === "Shalom") setShipping(20);
    }
  };

  // ðŸ“Œ Simular envÃ­o de pedido
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.nombre || !formData.email || !formData.direccion) {
      alert("Por favor, completa todos los campos obligatorios.");
      return;
    }

    alert(
      `âœ… Pedido realizado con Ã©xito\nGracias por tu compra, ${formData.nombre}\nEnvÃ­o: ${formData.envio} (S/ ${shipping})\nTotal: S/ ${getTotal()}`
    );

    localStorage.removeItem("cart"); // Vaciar carrito
    window.location.href = "/"; // Redirigir a inicio
  };

  if (cart.length === 0) {
    return (
      <Container sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="h5">ðŸ›’ No hay productos en tu carrito</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ mt: 4, mb: 6 }}>
      <Typography variant="h4" gutterBottom>
        ðŸ§¾ Finalizar Compra
      </Typography>

      <Grid container spacing={4}>
        {/* Formulario */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>
            Datos de envÃ­o y facturaciÃ³n
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Nombre completo"
              name="nombre"
              value={formData.nombre}
              onChange={handleChange}
              fullWidth
              margin="normal"
              required
            />
            <TextField
              label="Correo electrÃ³nico"
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              fullWidth
              margin="normal"
              required
            />
            <TextField
              label="DirecciÃ³n"
              name="direccion"
              value={formData.direccion}
              onChange={handleChange}
              fullWidth
              margin="normal"
              required
            />
            <TextField
              label="TelÃ©fono"
              name="telefono"
              value={formData.telefono}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="RUC"
              name="ruc"
              value={formData.ruc}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Nombre de empresa"
              name="empresa"
              value={formData.empresa}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <Typography variant="h6" sx={{ mt: 2 }}>
              MÃ©todo de envÃ­o
            </Typography>
            <RadioGroup
              name="envio"
              value={formData.envio}
              onChange={handleChange}
            >
              <FormControlLabel
                value="Olva"
                control={<Radio />}
                label="Delivery - Olva: S/ 15.00"
              />
              <FormControlLabel
                value="Shalom"
                control={<Radio />}
                label="Delivery - Shalom: S/ 20.00"
              />
            </RadioGroup>

            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              sx={{ mt: 3 }}
            >
              Realizar Pedido
            </Button>
          </form>
        </Grid>

        {/* Resumen */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>
            Resumen de compra
          </Typography>
          <Card sx={{ boxShadow: 4 }}>
            <CardContent>
              {cart.map((item) => (
                <div key={item._id} style={{ marginBottom: "10px" }}>
                  <Typography variant="body1">
                    {item.name} x {item.quantity}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    S/ {(item.price * item.quantity).toFixed(2)}
                  </Typography>
                  <Divider sx={{ my: 1 }} />
                </div>
              ))}

              <Typography variant="body1" sx={{ mt: 2 }}>
                Subtotal: <strong>S/ {getSubtotal().toFixed(2)}</strong>
              </Typography>
              <Typography variant="body1">
                EnvÃ­o ({formData.envio}): <strong>S/ {shipping.toFixed(2)}</strong>
              </Typography>
              <Divider sx={{ my: 2 }} />
              <Typography variant="h6" align="right" color="primary">
                Total: S/ {getTotal()}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Checkout;
