import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = () => {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    description: '',
    category: '',
    price: '',
    stock: '',
    image: null
  });

  // Cargar productos al iniciar
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/products');
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Manejar cambio en inputs
  const handleChange = (e) => {
    if (e.target.name === 'image') {
      setForm({ ...form, image: e.target.files[0] });
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };

  // Enviar nuevo producto
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', form.name);
      formData.append('description', form.description);
      formData.append('category', form.category);
      formData.append('price', form.price);
      formData.append('stock', form.stock);
      if (form.image) {
        formData.append('image', form.image);
      }

      await axios.post('http://localhost:4000/api/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setForm({
        name: '',
        description: '',
        category: '',
        price: '',
        stock: '',
        image: null
      });
      fetchProducts();
    } catch (err) {
      console.error(err);
    }
  };

  // Eliminar producto
  const handleDelete = async (id) => {
    if (window.confirm('¿Seguro que quieres eliminar este producto?')) {
      try {
        await axios.delete(`http://localhost:4000/api/products/${id}`);
        fetchProducts();
      } catch (err) {
        console.error(err);
      }
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Panel de Administración</h2>

      {/* Formulario */}
      <form onSubmit={handleSubmit} style={{ marginBottom: '30px' }}>
        <input type="text" name="name" placeholder="Nombre" value={form.name} onChange={handleChange} required />
        <input type="text" name="description" placeholder="Descripción" value={form.description} onChange={handleChange} required />
        <input type="text" name="category" placeholder="Categoría" value={form.category} onChange={handleChange} required />
        <input type="number" name="price" placeholder="Precio" value={form.price} onChange={handleChange} required />
        <input type="number" name="stock" placeholder="Stock" value={form.stock} onChange={handleChange} required />
        <input type="file" name="image" onChange={handleChange} />
        <button type="submit">Agregar Producto</button>
      </form>

      {/* Lista de productos */}
      <h3>Productos Existentes</h3>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Imagen</th>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Categoría</th>
            <th>Precio</th>
            <th>Stock</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((p) => (
              <tr key={p.id}>
                <td>
                  {p.image && <img src={`http://localhost:4000${p.image}`} alt={p.name} width="50" />}
                </td>
                <td>{p.name}</td>
                <td>{p.description}</td>
                <td>{p.category}</td>
                <td>{p.price}</td>
                <td>{p.stock}</td>
                <td>
                  <button onClick={() => handleDelete(p.id)}>Eliminar</button>
                </td>
              </tr>
            ))
          ) : (
            <tr><td colSpan="7">No hay productos</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Admin;
