// src/context/CartContext.js
import React, { createContext, useState, useEffect } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState(() => {
    const storedCart = localStorage.getItem("cart");
    return storedCart ? JSON.parse(storedCart) : [];
  });

  // Guardar carrito en localStorage
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  /**
   * ðŸ”¹ FunciÃ³n para comparar objetos de forma estable
   * Esto asegura que las variaciones se comparen correctamente
   */
  const areOptionsEqual = (a = {}, b = {}) => {
    const keysA = Object.keys(a).sort();
    const keysB = Object.keys(b).sort();
    if (keysA.length !== keysB.length) return false;
    return keysA.every((key) => a[key] === b[key]);
  };

  /**
   * âœ… AÃ±adir producto
   * Si mismo producto + mismas opciones â†’ incrementa
   * Si cambia algo â†’ Ã­tem nuevo
   */
  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingIndex = prevCart.findIndex(
        (item) =>
          item._id === product._id &&
          areOptionsEqual(item.options, product.options)
      );

      if (existingIndex >= 0) {
        // Incrementar cantidad si es el mismo
        const updatedCart = [...prevCart];
        updatedCart[existingIndex].quantity += 1;
        return updatedCart;
      } else {
        // Crear nuevo Ã­tem
        return [
          ...prevCart,
          {
            ...product,
            quantity: 1,
            cartId: Date.now() + Math.random()
          }
        ];
      }
    });
  };

  // âœ… Eliminar por cartId
  const removeFromCart = (cartId) => {
    setCart((prevCart) => prevCart.filter((item) => item.cartId !== cartId));
  };

  // âœ… Actualizar cantidad
  const updateQuantity = (cartId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(cartId);
    } else {
      setCart((prevCart) =>
        prevCart.map((item) =>
          item.cartId === cartId ? { ...item, quantity: newQuantity } : item
        )
      );
    }
  };

  // âœ… Vaciar carrito
  const clearCart = () => {
    setCart([]);
  };

  return (
    <CartContext.Provider
      value={{ cart, addToCart, removeFromCart, updateQuantity, clearCart }}
    >
      {children}
    </CartContext.Provider>
  );
};
