import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  IconButton
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

function Cart() {
  const [cart, setCart] = useState([]);

  // ðŸ“Œ Cargar carrito desde localStorage
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  // ðŸ“Œ Guardar carrito en localStorage
  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  // ðŸ“Œ Eliminar producto del carrito
  const removeFromCart = (id) => {
    const updated = cart.filter((item) => item._id !== id);
    updateCart(updated);
  };

  // ðŸ“Œ Cambiar cantidad
  const changeQuantity = (id, delta) => {
    const updated = cart.map((item) =>
      item._id === id
        ? { ...item, quantity: Math.max(1, item.quantity + delta) }
        : item
    );
    updateCart(updated);
  };

  // ðŸ“Œ Calcular total
  const getTotal = () =>
    cart.reduce((acc, item) => acc + item.price * item.quantity, 0).toFixed(2);

  if (cart.length === 0) {
    return (
      <Container sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="h5">ðŸ›’ Tu carrito estÃ¡ vacÃ­o</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        ðŸ›’ Carrito de Compras
      </Typography>
      <Grid container spacing={3}>
        {cart.map((item) => (
          <Grid item xs={12} key={item._id}>
            <Card sx={{ display: "flex", alignItems: "center" }}>
              <CardMedia
                component="img"
                sx={{ width: 120, height: 120, objectFit: "cover" }}
                image={item.image}
                alt={item.name}
              />
              <CardContent sx={{ flex: 1 }}>
                <Typography variant="h6">{item.name}</Typography>
                <Typography variant="body2" color="text.secondary">
                  Precio: S/ {item.price}
                </Typography>
                <Typography variant="body2">
                  Cantidad:{" "}
                  <Button
                    variant="outlined"
                    size="small"
                    onClick={() => changeQuantity(item._id, -1)}
                  >
                    -
                  </Button>
                  <span style={{ margin: "0 10px" }}>{item.quantity}</span>
                  <Button
                    variant="outlined"
                    size="small"
                    onClick={() => changeQuantity(item._id, 1)}
                  >
                    +
                  </Button>
                </Typography>
              </CardContent>
              <CardActions>
                <IconButton
                  color="error"
                  onClick={() => removeFromCart(item._id)}
                >
                  <DeleteIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Total y Checkout */}
      <Typography variant="h5" align="right" sx={{ mt: 3 }}>
        Total: S/ {getTotal()}
      </Typography>
      <Button
        variant="contained"
        color="primary"
        fullWidth
        sx={{ mt: 2 }}
        onClick={() => (window.location.href = "/checkout")}
      >
        Proceder al Pago
      </Button>
    </Container>
  );
}

export default Cart;
