import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Card,
  CardMedia,
  CardContent,
  IconButton,
  Button,
  Box,
  Divider
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { Link } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  // ðŸ“Œ Cargar carrito
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  // ðŸ“Œ Calcular total
  useEffect(() => {
    const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotal(totalPrice);
  }, [cart]);

  // ðŸ“Œ Aumentar cantidad
  const increaseQuantity = (index) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity += 1;
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // ðŸ“Œ Disminuir cantidad
  const decreaseQuantity = (index) => {
    const updatedCart = [...cart];
    if (updatedCart[index].quantity > 1) {
      updatedCart[index].quantity -= 1;
    }
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // ðŸ“Œ Eliminar producto
  const removeItem = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  return (
    <Container sx={{ mt: 4, mb: 6 }}>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: "bold", color: "#333", display: "flex", alignItems: "center" }}>
        <ShoppingCartIcon sx={{ mr: 1, fontSize: 32, color: "#1976d2" }} />
        Carrito de Compras
      </Typography>

      {cart.length === 0 ? (
        <Typography variant="h6" sx={{ color: "#777", mt: 3 }}>
          Tu carrito estÃ¡ vacÃ­o.  
          <Link to="/" style={{ textDecoration: "none", color: "#1976d2" }}>Ir a la tienda</Link>
        </Typography>
      ) : (
        <>
          {cart.map((item, index) => (
            <Card
              key={index}
              sx={{
                display: "flex",
                alignItems: "center",
                mb: 2,
                boxShadow: 4,
                borderRadius: 2,
                transition: "0.3s",
                "&:hover": { boxShadow: 8 }
              }}
            >
              <CardMedia
                component="img"
                sx={{
                  width: 140,
                  height: 140,
                  objectFit: "contain",
                  backgroundColor: "#f9f9f9",
                  borderRight: "1px solid #eee"
                }}
                image={item.image}
                alt={item.name}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography variant="h6" sx={{ fontWeight: "bold" }}>{item.name}</Typography>
                <Typography variant="body1" sx={{ color: "#1976d2", fontWeight: "bold" }}>
                  S/ {item.price.toFixed(2)}
                </Typography>

                <Box sx={{ display: "flex", alignItems: "center", mt: 1 }}>
                  <Typography variant="body2" sx={{ mr: 1, fontWeight: "bold" }}>
                    Cantidad:
                  </Typography>
                  <IconButton
                    onClick={() => decreaseQuantity(index)}
                    size="small"
                    sx={{
                      backgroundColor: "#eee",
                      "&:hover": { backgroundColor: "#ddd" }
                    }}
                  >
                    <RemoveIcon />
                  </IconButton>
                  <Typography variant="body1" sx={{ mx: 1, minWidth: "24px", textAlign: "center" }}>
                    {item.quantity}
                  </Typography>
                  <IconButton
                    onClick={() => increaseQuantity(index)}
                    size="small"
                    sx={{
                      backgroundColor: "#eee",
                      "&:hover": { backgroundColor: "#ddd" }
                    }}
                  >
                    <AddIcon />
                  </IconButton>

                  <IconButton
                    onClick={() => removeItem(index)}
                    color="error"
                    sx={{ ml: 2 }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          ))}

          <Divider sx={{ my: 3 }} />

          {/* Total y botÃ³n */}
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              backgroundColor: "#f5f5f5",
              padding: "16px",
              borderRadius: 2,
              boxShadow: 2
            }}
          >
            <Typography variant="h6" sx={{ fontWeight: "bold" }}>
              Total: S/ {total.toFixed(2)}
            </Typography>
            <Button
              variant="contained"
              color="primary"
              size="large"
              component={Link}
              to="/checkout"
              sx={{
                padding: "10px 20px",
                fontSize: "16px",
                fontWeight: "bold",
                borderRadius: "8px"
              }}
            >
              Proceder al pago
            </Button>
          </Box>
        </>
      )}
    </Container>
  );
}

export default Cart;
