import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import IconButton from "@mui/material/IconButton";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MenuIcon from "@mui/icons-material/Menu";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import DashboardIcon from "@mui/icons-material/Dashboard";
import HomeIcon from "@mui/icons-material/Home";
import AdminPanelSettingsIcon from "@mui/icons-material/AdminPanelSettings";

function Navbar() {
  const location = useLocation();
  const [anchorEl, setAnchorEl] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  // Verificar si el usuario es admin mediante token
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split(".")[1])); // Decodifica JWT
        if (payload.role && payload.role === "admin") {
          setIsAdmin(true);
        }
      } catch (e) {
        setIsAdmin(false);
      }
    }
  }, []);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const linkStyle = (path) => ({
    color: "#fff",
    marginRight: "15px",
    textDecoration: location.pathname === path ? "underline" : "none"
  });

  return (
    <AppBar position="static" sx={{ backgroundColor: "#1e1e1e" }}>
      <Toolbar>
        {/* Logo */}
        <Typography
          variant="h6"
          component={Link}
          to="/"
          sx={{
            flexGrow: 1,
            color: "#fff",
            textDecoration: "none",
            fontWeight: "bold",
            fontSize: "1.3rem"
          }}
        >
          🛒 Tienda Titilab
        </Typography>

        {/* Menú hamburguesa para móviles */}
        <IconButton
          edge="start"
          color="inherit"
          aria-label="menu"
          sx={{ display: { xs: "flex", md: "none" } }}
          onClick={handleMenu}
        >
          <MenuIcon />
        </IconButton>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          <MenuItem component={Link} to="/" onClick={handleClose}>
            <HomeIcon sx={{ mr: 1 }} /> Inicio
          </MenuItem>
          <MenuItem component={Link} to="/cart" onClick={handleClose}>
            <ShoppingCartIcon sx={{ mr: 1 }} /> Carrito
          </MenuItem>
          <MenuItem component={Link} to="/dashboards" onClick={handleClose}>
            <DashboardIcon sx={{ mr: 1 }} /> IoT Dashboards
          </MenuItem>
          {isAdmin && (
            <MenuItem component={Link} to="/admin" onClick={handleClose}>
              <AdminPanelSettingsIcon sx={{ mr: 1 }} /> Admin
            </MenuItem>
          )}
        </Menu>

        {/* Botones normales para escritorio */}
        <div style={{ display: "flex", alignItems: "center" }}>
          <Button
            component={Link}
            to="/"
            sx={{ ...linkStyle("/"), display: { xs: "none", md: "inline-flex" } }}
            startIcon={<HomeIcon />}
          >
            Inicio
          </Button>
          <Button
            component={Link}
            to="/cart"
            sx={{ ...linkStyle("/cart"), display: { xs: "none", md: "inline-flex" } }}
            startIcon={<ShoppingCartIcon />}
          >
            Carrito
          </Button>
          <Button
            component={Link}
            to="/dashboards"
            sx={{ ...linkStyle("/dashboards"), display: { xs: "none", md: "inline-flex" } }}
            startIcon={<DashboardIcon />}
          >
            IoT Dashboards
          </Button>
          {isAdmin && (
            <Button
              component={Link}
              to="/admin"
              sx={{ ...linkStyle("/admin"), display: { xs: "none", md: "inline-flex" } }}
              startIcon={<AdminPanelSettingsIcon />}
            >
              Admin
            </Button>
          )}
        </div>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
