import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  IconButton,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:4000";

function Admin() {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    stock: "",
    image: null,
  });

  // Lista de categorías predefinidas
  const categoryOptions = [
    "Robótica",
    "Impresión 3D",
    "Minería",
    "Pesquería",
    "Ganadería",
    "Construcción",
    "Militar",
    "Microcontroladores",
    "Arduino",
    "ESP32",
    "Raspberry",
    "Sensores",
    "Componentes",
  ];

  // ✅ Verificar login y cargar datos
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/admin-login";
      return;
    }
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      if (payload.exp * 1000 < Date.now()) {
        localStorage.removeItem("token");
        window.location.href = "/admin-login";
        return;
      }
    } catch {
      localStorage.removeItem("token");
      window.location.href = "/admin-login";
      return;
    }
    fetchProducts();
    fetchOrders();
  }, []);

  // 📌 Obtener productos
  const fetchProducts = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) setProducts(await res.json());
  };

  // 📌 Obtener pedidos
  const fetchOrders = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/orders`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) setOrders(await res.json());
  };

  // 📌 Manejo de inputs productos
  const handleChange = (e) => {
    if (e.target.name === "image") {
      setFormData({ ...formData, image: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // 📌 Agregar producto
  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.keys(formData).forEach((key) => {
      form.append(key, formData[key]);
    });

    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: form,
    });

    if (res.ok) {
      alert("✅ Producto agregado");
      setFormData({
        name: "",
        description: "",
        category: "",
        price: "",
        stock: "",
        image: null,
      });
      fetchProducts();
    } else {
      alert("❌ Error al agregar producto");
    }
  };

  // 📌 Eliminar producto
  const deleteProduct = async (id) => {
    if (!window.confirm("¿Eliminar producto?")) return;
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) {
      alert("🗑️ Producto eliminado");
      setProducts(products.filter((p) => p._id !== id));
    }
  };

  // 📌 Cambiar estado pedido
  const updateOrderStatus = async (id, status) => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/orders/${id}/status`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ status }),
    });
    if (res.ok) {
      fetchOrders();
    }
  };

  // 📌 Cerrar sesión
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/admin-login";
  };

  // 📌 Filtrar pedidos por estado
  const filterOrders = (estado) =>
    orders.filter((o) => (o.status || "pendiente") === estado);

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        📦 Panel de Administración
      </Typography>
      <Button
        variant="outlined"
        color="secondary"
        onClick={handleLogout}
        sx={{ mb: 2 }}
      >
        Cerrar Sesión
      </Button>

      {/* === FORMULARIO PRODUCTOS === */}
      <form onSubmit={handleSubmit} style={{ marginBottom: "30px" }}>
        <Typography variant="h5">➕ Agregar Producto</Typography>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Nombre"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </Grid>

          <Grid item xs={12} sm={6} md={4}>
            <TextField
              select
              fullWidth
              label="Categoría"
              name="category"
              value={formData.category}
              onChange={handleChange}
              required
            >
              {categoryOptions.map((cat) => (
                <MenuItem key={cat} value={cat}>
                  {cat}
                </MenuItem>
              ))}
            </TextField>
          </Grid>

          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Precio"
              name="price"
              type="number"
              value={formData.price}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              label="Stock"
              name="stock"
              type="number"
              value={formData.stock}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={8}>
            <TextField
              fullWidth
              label="Descripción"
              name="description"
              multiline
              rows={2}
              value={formData.description}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <Button variant="outlined" component="label" fullWidth>
              Subir Imagen
              <input type="file" name="image" hidden onChange={handleChange} />
            </Button>
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" startIcon={<AddIcon />}>
              Agregar Producto
            </Button>
          </Grid>
        </Grid>
      </form>

      <Divider sx={{ my: 3 }} />

      {/* === LISTA PRODUCTOS === */}
      <Typography variant="h5" gutterBottom>
        🛒 Lista de Productos
      </Typography>
      <List>
        {products.map((product) => (
          <ListItem key={product._id}>
            <ListItemText
              primary={`${product.name} (${product.category})`}
              secondary={`Precio: S/. ${product.price} | Stock: ${product.stock}`}
            />
            <ListItemSecondaryAction>
              <IconButton
                edge="end"
                color="error"
                onClick={() => deleteProduct(product._id)}
              >
                <DeleteIcon />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>

      <Divider sx={{ my: 4 }} />

      {/* === LISTA PEDIDOS === */}
      {["pendiente", "enviado", "completado"].map((estado) => (
        <div key={estado}>
          <Typography variant="h5" sx={{ mt: 3 }}>
            {estado === "pendiente" && "📌 Pendientes"}
            {estado === "enviado" && "📦 Enviados"}
            {estado === "completado" && "✅ Completados"}
          </Typography>
          <List>
            {filterOrders(estado).map((order) => (
              <ListItem key={order.id} alignItems="flex-start">
                <ListItemText
                  primary={`Pedido #${order.id} - ${order.nombre} (${order.email})`}
                  secondary={
                    <>
                      Dirección: {order.direccion} - Tel: {order.telefono}
                      <br />
                      Total: S/. {order.total}
                      <br />
                      Productos:{" "}
                      {order.cart
                        .map((item) => `${item.name} (x${item.quantity})`)
                        .join(", ")}
                    </>
                  }
                />
                <FormControl size="small" sx={{ minWidth: 120 }}>
                  <Select
                    value={order.status || "pendiente"}
                    onChange={(e) =>
                      updateOrderStatus(order.id, e.target.value)
                    }
                  >
                    <MenuItem value="pendiente">Pendiente</MenuItem>
                    <MenuItem value="enviado">Enviado</MenuItem>
                    <MenuItem value="completado">Completado</MenuItem>
                  </Select>
                </FormControl>
              </ListItem>
            ))}
          </List>
        </div>
      ))}
    </Container>
  );
}

export default Admin;
