import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import {
  Container,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  CircularProgress,
  Snackbar,
  Alert,
  Box,
  TextField,
  Chip
} from "@mui/material";
import { CartContext } from "../context/CartContext";
import MemoryIcon from "@mui/icons-material/Memory";
import PrecisionManufacturingIcon from "@mui/icons-material/PrecisionManufacturing";
import ConstructionIcon from "@mui/icons-material/Construction";
import AgricultureIcon from "@mui/icons-material/Agriculture";
import ScienceIcon from "@mui/icons-material/Science";
import SchoolIcon from "@mui/icons-material/School";
import AllInclusiveIcon from "@mui/icons-material/AllInclusive";

function Home() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({ open: false, message: "" });
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Todos");
  const { addToCart } = useContext(CartContext);

  // Lista de categor√≠as con √≠conos
  const categoryList = [
    { name: "Todos", icon: <AllInclusiveIcon /> },
    { name: "Microcontroladores", icon: <MemoryIcon /> },
    { name: "Arduino", icon: <PrecisionManufacturingIcon /> },
    { name: "ESP32", icon: <MemoryIcon /> },
    { name: "Raspberry", icon: <MemoryIcon /> },
    { name: "Sensores", icon: <ScienceIcon /> },
    { name: "Componentes", icon: <ConstructionIcon /> },
    { name: "Miner√≠a", icon: <ConstructionIcon /> },
    { name: "Pesquer√≠a", icon: <ScienceIcon /> },
    { name: "Ganader√≠a", icon: <AgricultureIcon /> },
    { name: "Construcci√≥n", icon: <ConstructionIcon /> },
    { name: "Militar", icon: <PrecisionManufacturingIcon /> },
    { name: "Agricultura", icon: <AgricultureIcon /> },
    { name: "Impresi√≥n 3D", icon: <PrecisionManufacturingIcon /> },
    { name: "Rob√≥tica", icon: <PrecisionManufacturingIcon /> },
    { name: "Cursos", icon: <SchoolIcon /> }
  ];

  useEffect(() => {
    axios
      .get("http://localhost:4000/api/products")
      .then((res) => {
        setProducts(res.data);
        setFilteredProducts(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error al obtener productos:", err);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    let tempProducts = [...products];

    if (selectedCategory !== "Todos") {
      tempProducts = tempProducts.filter(
        (p) => p.category && p.category.toLowerCase() === selectedCategory.toLowerCase()
      );
    }

    if (search.trim() !== "") {
      tempProducts = tempProducts.filter(
        (p) =>
          p.name.toLowerCase().includes(search.toLowerCase()) ||
          p.description.toLowerCase().includes(search.toLowerCase())
      );
    }

    setFilteredProducts(tempProducts);
  }, [search, selectedCategory, products]);

  const handleAddToCart = (product) => {
    addToCart(product);
    setSnackbar({
      open: true,
      message: `${product.name} agregado al carrito üõí`
    });
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: "center", mt: 10 }}>
        <CircularProgress />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Cargando productos...
        </Typography>
      </Container>
    );
  }

  return (
    <>
      {/* Hero Banner */}
      <Box
        sx={{
          width: "100%",
          height: { xs: 80, md: 140 },
          backgroundImage: "url('https://via.placeholder.com/1600x400?text=Titilab+Store')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "#fff",
          textAlign: "center",
          mb: 4
        }}
      >
        <Typography
          variant="h4"
          fontWeight="bold"
          sx={{
            bgcolor: "rgba(0,0,0,0.5)",
            p: 2,
            borderRadius: 2,
            maxWidth: "90%"
          }}
        >
          Bienvenido a Titilab Store ‚Äì Componentes y Soluciones IoT
        </Typography>
      </Box>

      <Container maxWidth="xl">
        {/* Search Bar */}
        <Box sx={{ mb: 3 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Buscar productos..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </Box>

        {/* Categor√≠as Modernas */}
        <Box
          sx={{
            display: "flex",
            flexWrap: "wrap",
            gap: 1.5,
            mb: 4
          }}
        >
          {categoryList.map((cat) => (
            <Chip
              key={cat.name}
              icon={cat.icon}
              label={cat.name}
              onClick={() => setSelectedCategory(cat.name)}
              color={selectedCategory === cat.name ? "primary" : "default"}
              sx={{
                px: 2,
                py: 1,
                fontWeight: "bold",
                fontSize: "0.9rem",
                borderRadius: "12px",
                cursor: "pointer"
              }}
            />
          ))}
        </Box>

        {/* Productos */}
        <Typography variant="h5" gutterBottom fontWeight="bold">
          {selectedCategory === "Todos" ? "Todos los Productos" : selectedCategory}
        </Typography>
        <Grid container spacing={3}>
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={product._id}>
                <Card
                  sx={{
                    height: "100%",
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "space-between",
                    transition: "transform 0.2s ease, box-shadow 0.2s ease",
                    "&:hover": {
                      transform: "scale(1.03)",
                      boxShadow: 6
                    }
                  }}
                >
                  <Link
                    to={`/producto/${product._id}`}
                    style={{ textDecoration: "none", color: "inherit" }}
                  >
                    <CardMedia
                      component="img"
                      height="200"
                      image={product.image}
                      alt={product.name}
                      sx={{
                        objectFit: "contain",
                        bgcolor: "#f5f5f5",
                        borderBottom: "1px solid #ddd"
                      }}
                    />
                  </Link>

                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography
                      variant="subtitle1"
                      fontWeight="bold"
                      noWrap
                      component={Link}
                      to={`/producto/${product._id}`}
                      style={{ textDecoration: "none", color: "inherit" }}
                    >
                      {product.name}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="text.secondary"
                      sx={{
                        mb: 1,
                        minHeight: 40,
                        overflow: "hidden",
                        textOverflow: "ellipsis",
                        display: "-webkit-box",
                        WebkitLineClamp: 2,
                        WebkitBoxOrient: "vertical"
                      }}
                    >
                      {product.description}
                    </Typography>
                    <Typography variant="h6" color="primary">
                      S/ {Number(product.price).toFixed(2)}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      variant="contained"
                      size="small"
                      fullWidth
                      onClick={() => handleAddToCart(product)}
                    >
                      Agregar al carrito
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))
          ) : (
            <Grid item xs={12}>
              <Typography variant="body1" color="text.secondary">
                No se encontraron productos para esta b√∫squeda.
              </Typography>
            </Grid>
          )}
        </Grid>

        {/* Snackbar */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={2000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          anchorOrigin={{ vertical: "top", horizontal: "center" }}
        >
          <Alert severity="success" sx={{ width: "100%" }}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Container>
    </>
  );
}

export default Home;
