import React, { useEffect, useState } from "react";
import departamentos from "../data/ubigeo_peru_2016_departamentos.json";
import provincias from "../data/ubigeo_peru_2016_provincias.json";
import distritos from "../data/ubigeo_peru_2016_distritos.json";
import {
  Container,
  Typography,
  Grid,
  TextField,
  Button,
  Card,
  CardContent,
  Divider,
  MenuItem,
  Select,
  FormControl,
  InputLabel
} from "@mui/material";

function Checkout() {
  const [cart, setCart] = useState([]);
  const [selectedDepartamento, setSelectedDepartamento] = useState("");
  const [selectedProvincia, setSelectedProvincia] = useState("");
  const [selectedDistrito, setSelectedDistrito] = useState("");
  const [delivery, setDelivery] = useState(15); // Default Olva

  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    direccion: "",
    telefono: "",
    ruc: "",
    empresa: ""
  });

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  const getSubtotal = () =>
    cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const getTotal = () => (getSubtotal() + delivery).toFixed(2);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.nombre || !formData.email || !formData.direccion) {
      alert("Por favor, completa todos los campos obligatorios.");
      return;
    }
    alert(`âœ… Pedido realizado con Ã©xito\nGracias por tu compra, ${formData.nombre}`);
    localStorage.removeItem("cart");
    window.location.href = "/";
  };

  // Filtrar provincias segÃºn departamento
  const provinciasFiltradas = selectedDepartamento
    ? provincias.filter(p => p.departamento_id === selectedDepartamento)
    : [];

  // Filtrar distritos segÃºn provincia
  const distritosFiltrados = selectedProvincia
    ? distritos.filter(d => d.provincia_id === selectedProvincia)
    : [];

  if (cart.length === 0) {
    return (
      <Container sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="h5">ðŸ›’ No hay productos en tu carrito</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>ðŸ§¾ Finalizar Compra</Typography>

      <Grid container spacing={4}>
        {/* Formulario */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>Datos de envÃ­o</Typography>
          <form onSubmit={handleSubmit}>
            <TextField label="Nombre completo" name="nombre" value={formData.nombre} onChange={handleChange} fullWidth margin="normal" required />
            <TextField label="Correo electrÃ³nico" type="email" name="email" value={formData.email} onChange={handleChange} fullWidth margin="normal" required />
            <TextField label="DirecciÃ³n" name="direccion" value={formData.direccion} onChange={handleChange} fullWidth margin="normal" required />
            <TextField label="TelÃ©fono" name="telefono" value={formData.telefono} onChange={handleChange} fullWidth margin="normal" />
            <TextField label="RUC" name="ruc" value={formData.ruc} onChange={handleChange} fullWidth margin="normal" />
            <TextField label="Nombre de Empresa" name="empresa" value={formData.empresa} onChange={handleChange} fullWidth margin="normal" />

            {/* Departamento */}
            <FormControl fullWidth margin="normal">
              <InputLabel>Departamento</InputLabel>
              <Select value={selectedDepartamento} onChange={(e) => { setSelectedDepartamento(e.target.value); setSelectedProvincia(""); }}>
                {departamentos.map(dep => (
                  <MenuItem key={dep.id} value={dep.id}>{dep.nombre}</MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Provincia */}
            <FormControl fullWidth margin="normal" disabled={!selectedDepartamento}>
              <InputLabel>Provincia</InputLabel>
              <Select value={selectedProvincia} onChange={(e) => { setSelectedProvincia(e.target.value); setSelectedDistrito(""); }}>
                {provinciasFiltradas.map(prov => (
                  <MenuItem key={prov.id} value={prov.id}>{prov.nombre}</MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Distrito */}
            <FormControl fullWidth margin="normal" disabled={!selectedProvincia}>
              <InputLabel>Distrito</InputLabel>
              <Select value={selectedDistrito} onChange={(e) => setSelectedDistrito(e.target.value)}>
                {distritosFiltrados.map(dist => (
                  <MenuItem key={dist.id} value={dist.id}>{dist.nombre}</MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Tipo de Delivery */}
            <FormControl fullWidth margin="normal">
              <InputLabel>Courier</InputLabel>
              <Select value={delivery} onChange={(e) => setDelivery(Number(e.target.value))}>
                <MenuItem value={15}>Olva Courier - S/ 15.00</MenuItem>
                <MenuItem value={20}>Shalom - S/ 20.00</MenuItem>
              </Select>
            </FormControl>

            <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
              Realizar Pedido
            </Button>
          </form>
        </Grid>

        {/* Resumen */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>Resumen de compra</Typography>
          <Card>
            <CardContent>
              {cart.map((item) => (
                <div key={item._id} style={{ marginBottom: "10px" }}>
                  <Typography variant="body1">{item.name} x {item.quantity}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    S/ {(item.price * item.quantity).toFixed(2)}
                  </Typography>
                  <Divider sx={{ my: 1 }} />
                </div>
              ))}
              <Typography variant="body1">Subtotal: S/ {getSubtotal().toFixed(2)}</Typography>
              <Typography variant="body1">EnvÃ­o: S/ {delivery.toFixed(2)}</Typography>
              <Typography variant="h6" align="right">Total: S/ {getTotal()}</Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Checkout;
