// src/context/CartContext.js
import React, { createContext, useState, useEffect } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState(() => {
    const storedCart = localStorage.getItem("cart");
    return storedCart ? JSON.parse(storedCart) : [];
  });

  // Guardar en localStorage cuando cambia el carrito
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  /**
   * ✅ Añadir producto al carrito
   * Si existe (por id y opciones) → incrementar cantidad
   * Si no existe → agregar nuevo con cantidad = 1
   */
  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingIndex = prevCart.findIndex(
        (item) =>
          item._id === product._id &&
          JSON.stringify(item.options || {}) ===
            JSON.stringify(product.options || {})
      );

      if (existingIndex >= 0) {
        // Incrementar cantidad si ya existe
        const updatedCart = [...prevCart];
        updatedCart[existingIndex].quantity += 1;
        return updatedCart;
      } else {
        // Agregar como nuevo ítem
        return [
          ...prevCart,
          {
            ...product,
            quantity: 1,
            cartId: Date.now() + Math.random() // ID único
          }
        ];
      }
    });
  };

  /**
   * ✅ Eliminar producto usando cartId (único en cada ítem)
   */
  const removeFromCart = (cartId) => {
    setCart((prevCart) => prevCart.filter((item) => item.cartId !== cartId));
  };

  /**
   * ✅ Actualizar cantidad de un ítem específico
   */
  const updateQuantity = (cartId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(cartId);
    } else {
      setCart((prevCart) =>
        prevCart.map((item) =>
          item.cartId === cartId ? { ...item, quantity: newQuantity } : item
        )
      );
    }
  };

  /**
   * ✅ Vaciar carrito
   */
  const clearCart = () => {
    setCart([]);
  };

  return (
    <CartContext.Provider
      value={{ cart, addToCart, removeFromCart, updateQuantity, clearCart }}
    >
      {children}
    </CartContext.Provider>
  );
};
