import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  IconButton,
  MenuItem,
  Select,
  FormControl,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Card,
  CardMedia,
  Box,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import SaveIcon from "@mui/icons-material/Save";
import CancelIcon from "@mui/icons-material/Cancel";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:4000";

function Admin() {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    stock: "",
    image: null,
  });
  const [editingProduct, setEditingProduct] = useState(null);
  const [editData, setEditData] = useState({});

  const categoryOptions = [
    "Robótica", "Impresión 3D", "Minería", "Pesquería", "Ganadería", "Construcción", "Militar",
    "Microcontroladores", "Arduino", "ESP32", "Raspberry", "Sensores", "Componentes",
  ];

  // Check login token and fetch data
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return redirectLogin();
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      if (payload.exp * 1000 < Date.now()) return redirectLogin();
    } catch {
      return redirectLogin();
    }
    fetchProducts();
    fetchOrders();
  }, []);

  const redirectLogin = () => {
    localStorage.removeItem("token");
    window.location.href = "/admin-login";
  };

  const fetchProducts = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) setProducts(await res.json());
  };

  const fetchOrders = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/orders`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) setOrders(await res.json());
  };

  const handleChange = (e) => {
    if (e.target.name === "image") {
      setFormData({ ...formData, image: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.keys(formData).forEach((key) => form.append(key, formData[key]));

    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: form,
    });

    if (res.ok) {
      alert("✅ Producto agregado");
      resetForm();
      fetchProducts();
    } else {
      alert("❌ Error al agregar producto");
    }
  };

  const resetForm = () => {
    setFormData({ name: "", description: "", category: "", price: "", stock: "", image: null });
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("¿Eliminar producto?")) return;
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) {
      alert("🗑️ Producto eliminado");
      setProducts(products.filter((p) => p._id !== id));
    }
  };

  const startEdit = (product) => {
    setEditingProduct(product._id);
    setEditData({ ...product });
  };

  const cancelEdit = () => {
    setEditingProduct(null);
    setEditData({});
  };

  const saveEdit = async () => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/products/${editingProduct}`, {
      method: "PUT",
      headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
      body: JSON.stringify(editData),
    });
    if (res.ok) {
      alert("✅ Producto actualizado");
      fetchProducts();
      cancelEdit();
    } else {
      alert("❌ Error al actualizar");
    }
  };

  const updateOrderStatus = async (id, status) => {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BACKEND_URL}/api/orders/${id}/status`, {
      method: "PUT",
      headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
      body: JSON.stringify({ status }),
    });
    if (res.ok) fetchOrders();
  };

  const handleLogout = () => redirectLogin();

  const filterOrders = (estado) =>
    orders.filter((o) => (o.status || "pendiente") === estado);

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>📦 Panel de Administración</Typography>
      <Button variant="outlined" color="secondary" onClick={handleLogout} sx={{ mb: 2 }}>
        Cerrar Sesión
      </Button>

      {/* FORM Add Product */}
      <Card sx={{ p: 3, mb: 4, boxShadow: 3 }}>
        <Typography variant="h5" gutterBottom>➕ Agregar Producto</Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={4}>
              <TextField fullWidth label="Nombre" name="name" value={formData.name} onChange={handleChange} required />
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField select fullWidth label="Categoría" name="category" value={formData.category} onChange={handleChange} required>
                {categoryOptions.map((cat) => <MenuItem key={cat} value={cat}>{cat}</MenuItem>)}
              </TextField>
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField fullWidth label="Precio (S/.)" name="price" type="number" value={formData.price} onChange={handleChange} required />
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField fullWidth label="Stock" name="stock" type="number" value={formData.stock} onChange={handleChange} required />
            </Grid>
            <Grid item xs={12} sm={8}>
              <TextField fullWidth label="Descripción" name="description" multiline rows={3} value={formData.description} onChange={handleChange} />
            </Grid>
            <Grid item xs={12} sm={4} textAlign="center">
              <Button variant="outlined" component="label" fullWidth>📤 Subir Imagen
                <input type="file" name="image" accept="image/*" hidden onChange={handleChange} />
              </Button>
              {formData.image && (
                <CardMedia component="img" image={URL.createObjectURL(formData.image)} alt="Vista previa" sx={{ mt: 2, height: 150, objectFit: "cover" }} />
              )}
            </Grid>
            <Grid item xs={12}>
              <Button type="submit" variant="contained" startIcon={<AddIcon />} fullWidth>Agregar Producto</Button>
            </Grid>
          </Grid>
        </form>
      </Card>

      {/* Products List */}
      <Typography variant="h5" gutterBottom>🛒 Lista de Productos</Typography>
      <List>
        {products.map((product) => (
          <ListItem key={product._id}>
            {editingProduct === product._id ? (
              <>
                <TextField value={editData.name} onChange={(e) => setEditData({ ...editData, name: e.target.value })} />
                <TextField value={editData.price} onChange={(e) => setEditData({ ...editData, price: e.target.value })} />
                <TextField value={editData.stock} onChange={(e) => setEditData({ ...editData, stock: e.target.value })} />
                <IconButton onClick={saveEdit}><SaveIcon /></IconButton>
                <IconButton onClick={cancelEdit}><CancelIcon /></IconButton>
              </>
            ) : (
              <>
                <ListItemText primary={`${product.name} (${product.category})`} secondary={`S/. ${product.price} | Stock: ${product.stock}`} />
                <IconButton onClick={() => startEdit(product)}><EditIcon /></IconButton>
                <IconButton color="error" onClick={() => deleteProduct(product._id)}><DeleteIcon /></IconButton>
              </>
            )}
          </ListItem>
        ))}
      </List>

      <Divider sx={{ my: 4 }} />

      {/* Orders */}
      {["pendiente", "enviado", "completado"].map((estado) => (
        <Box key={estado} sx={{ mb: 4 }}>
          <Typography variant="h5">
            {estado === "pendiente" && "📌 Pendientes"}
            {estado === "enviado" && "📦 Enviados"}
            {estado === "completado" && "✅ Completados"}
          </Typography>
          <List>
            {filterOrders(estado).map((order) => (
              <ListItem key={order.id} alignItems="flex-start">
                <ListItemText primary={`Pedido #${order.id} - ${order.nombre}`} secondary={`Total: S/. ${order.total}`} />
                <FormControl size="small" sx={{ minWidth: 120 }}>
                  <Select value={order.status || "pendiente"} onChange={(e) => updateOrderStatus(order.id, e.target.value)}>
                    <MenuItem value="pendiente">Pendiente</MenuItem>
                    <MenuItem value="enviado">Enviado</MenuItem>
                    <MenuItem value="completado">Completado</MenuItem>
                  </Select>
                </FormControl>
              </ListItem>
            ))}
          </List>
        </Box>
      ))}
    </Container>
  );
}

export default Admin;
