import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Container, Typography, Grid, Card, CardMedia, CardContent, CardActions, Button, MenuItem, Select } from "@mui/material";
import { CartContext } from "../context/CartContext";

function Home() {
  const [products, setProducts] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    axios.get("http://localhost:4000/api/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.error(err));
  }, []);

  const handleOptionChange = (productId, optionType, value) => {
    setSelectedOptions((prev) => ({
      ...prev,
      [productId]: {
        ...prev[productId],
        [optionType]: value
      }
    }));
  };

  const handleAddToCart = (product) => {
    const options = selectedOptions[product._id] || {};
    addToCart({ ...product, options });
  };

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        Productos
      </Typography>
      <Grid container spacing={3}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product._id}>
            <Card>
              <CardMedia component="img" height="200" image={product.image} alt={product.name} />
              <CardContent>
                <Typography variant="h6">{product.name}</Typography>
                <Typography variant="h6" color="primary">
                  S/ {product.price}
                </Typography>

                {/* Ejemplo: Selector de color */}
                <Select
                  value={selectedOptions[product._id]?.color || ""}
                  onChange={(e) => handleOptionChange(product._id, "color", e.target.value)}
                  displayEmpty
                  fullWidth
                  sx={{ mt: 1 }}
                >
                  <MenuItem value="">Seleccionar Color</MenuItem>
                  <MenuItem value="Rojo">Rojo</MenuItem>
                  <MenuItem value="Azul">Azul</MenuItem>
                </Select>

                {/* Ejemplo: Selector de talla */}
                <Select
                  value={selectedOptions[product._id]?.talla || ""}
                  onChange={(e) => handleOptionChange(product._id, "talla", e.target.value)}
                  displayEmpty
                  fullWidth
                  sx={{ mt: 1 }}
                >
                  <MenuItem value="">Seleccionar Talla</MenuItem>
                  <MenuItem value="S">S</MenuItem>
                  <MenuItem value="M">M</MenuItem>
                  <MenuItem value="L">L</MenuItem>
                </Select>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  fullWidth
                  onClick={() => handleAddToCart(product)}
                >
                  Agregar al carrito
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default Home;
